import os
import time

import boto3
import botocore
from botocore.config import Config

region = os.environ["AWS_REGION"]
spacing = 2

config = Config(retries={"max_attempts": 5, "mode": "standard"})
ssm_client = boto3.client("ssm", region_name=region, config=config)


def function_start(event, context):
    # Get parameters
    association_ids = event.get("AssociationIds")

    # Testing parameters existence
    if association_ids is None or len(association_ids) == 0:
        raise ValueError("association_ids is a required field.")

    response = bulk_delete_association(association_ids, spacing)

    # Return outputs
    return response


def bulk_delete_association(association_ids, spacing):
    association_ids_failed_delete = []

    # Loop through each association_ids
    for curr_id in association_ids:
        # delete each association_id
        response = delete_association(curr_id)

        # Append to list of failed association ids on error
        if response is not None:
            association_ids_failed_delete.append(
                {"association_id": curr_id, "Response": str(response)}
            )

        time.sleep(float(spacing))
    # Determine if bulk association delete failed
    is_successful = False
    if len(association_ids_failed_delete) == 0:
        is_successful = True

    return {
        "is_successful": is_successful,
        "association_ids_failed_delete": association_ids_failed_delete,
    }


def delete_association(association_id):
    error = None
    hasPermissions = True

    try:
        # Try to delete
        ssm_client.delete_association(AssociationId=association_id)
    except ssm_client.exceptions.AssociationDoesNotExist as e:
        error = e
    except ssm_client.exceptions.TooManyUpdates as e:
        # raise error if it is stuck with the previous association.
        raise e
    except botocore.exceptions.ClientError as e:
        # If there is a permission error, fail early
        if e.response["Error"]["Code"].strip() == "AccessDeniedException":
            hasPermissions = False
        else:
            # other error, return message
            error = e
    except botocore.exceptions.NoCredentialsError as e:
        hasPermissions = False
    except Exception as e:
        # Return error if it fails
        error = e
    if not hasPermissions:
        raise PermissionError(
            "Role provided does not have permission to perform ssm:DeleteAssociation"
        )
    return error
