import time

import boto3

ec2_client = boto3.client("ec2")
ssm_client = boto3.client("ssm")


def extend_ebs(event, context):
    platform_type = None
    if event["instance_id"] != "":
        platform_type = get_operating_system_by_instance_id(
            event["instance_id"]
        )

    if platform_type == "Windows":
        validate_windows_partition_size(
            event["instance_id"], event["drive_letter"], event["target_size"], event["volume_id"]
        )

    snapshot_id = create_snapshot(
        event["volume_id"], event["current_execution_id"]
    )

    extend_ebs_volume(event["volume_id"], int(event["target_size"]))

    if not event["keep_snapshot"]:
        delete_snapshot(snapshot_id)

    if event["instance_id"] != "":
        if platform_type == "Linux":
            return extend_file_system_for_linux(
                event["instance_id"], event["mount_point"]
            )
        elif platform_type == "Windows":
            return extend_file_system_for_windows(
                event["instance_id"],
                event["drive_letter"],
                event["target_size"],
            )
        else:
            raise Exception("Unsupported Platform")
    return "volume extended successfully"


def extend_ebs_volume(volume_id, target_size):
    ec2_client.modify_volume(
        VolumeId=volume_id,
        Size=target_size,
    )
    while True:
        response = ec2_client.describe_volumes(VolumeIds=[volume_id])
        if response["Volumes"][0]["Size"] == target_size:
            return "success"
        time.sleep(3)


def get_operating_system_by_instance_id(instance_id):
    try:
        os_type = ssm_client.describe_instance_information(
            InstanceInformationFilterList=[
                {"key": "InstanceIds", "valueSet": [instance_id]}
            ]
        )
        if len(os_type["InstanceInformationList"]) > 0:
            return os_type["InstanceInformationList"][0]["PlatformType"]
        else:
            raise Exception("The instance must be managed by system manager")
    except Exception as e:
        raise Exception(e)


def extend_file_system_for_linux(instance_id, mount_point):
    response = ssm_client.send_command(
        InstanceIds=[instance_id],
        DocumentName="AWS-RunShellScript",
        TimeoutSeconds=500,
        Parameters={
            "commands": [
                "#!/bin/bash",
                "set -x",
                "findmnt -T {} || exit 1".format(
                    mount_point
                ),  # make sure that the mount point is valid
                "partition=`findmnt -T {} | awk '{{print $2}}' | sed -n '2 p'`".format(
                    mount_point
                ),
                "deviceName=`lsblk -npo pkname $partition`",
                "partitionNumber=`echo ${partition#$deviceName} | tr -Cd 0-9`",
                "sudo growpart $deviceName $partitionNumber",
                "sudo xfs_growfs -d {} || sudo resize2fs $partition".format(
                    mount_point
                ),
            ]
        },
    )
    command_id = response["Command"]["CommandId"]
    status, status_details = get_command_status_with_wait(
        instance_id, command_id
    )
    if status_details == "Failed":
        raise Exception("Error extending the file system")
    return "volume extended successfully"


def create_snapshot(volume_id, exec_id):
    try:
        response = ec2_client.create_snapshot(
            Description="a snapshot before the volume resizing",
            VolumeId=volume_id,
            TagSpecifications=[
                {
                    "ResourceType": "snapshot",
                    "Tags": [{"Key": "execution_id", "Value": exec_id}],
                },
            ],
        )
        return response["SnapshotId"]
    except Exception as e:
        raise Exception(e)


def delete_snapshot(snapshot_id):
    try:
        ec2_client.delete_snapshot(
            SnapshotId=snapshot_id,
        )
    except Exception as e:
        raise Exception(e)


def extend_file_system_for_windows(instance_id, drive_letter, size):
    MAX_RETRIALS_NUM = 3
    WAITING_STATUS = ["Pending", "InProgress", "Delayed"]
    response = ssm_client.send_command(
        InstanceIds=[instance_id],
        DocumentName="AWS-RunPowerShellScript",
        TimeoutSeconds=500,
        Parameters={
            "commands": [
                "Update-HostStorageCache",
                "$partitions = Get-Partition | Where-Object { $_.DriveLetter }",
                "if (-not ($partitions | Where-Object {{ $_.DriveLetter -eq '{}' }}))".format(drive_letter),
                "{exit 1}",
                "$maxSize = Get-PartitionSupportedSize -DriveLetter {}".format(drive_letter),
                "Resize-Partition -DriveLetter {} -Size $maxSize.SizeMax".format(drive_letter)
            ]
        },
    )
    command_id = response["Command"]["CommandId"]
    status, status_details = get_command_status_with_wait(
        instance_id, command_id
    )

    for retries in range(MAX_RETRIALS_NUM):
        if status in WAITING_STATUS:
            time.sleep(10)
            status, status_details = get_command_status_with_wait(
            instance_id, command_id
            )

    if status_details == "Failed":
        raise Exception("Failed Extending the partition")
    return "volume extended successfully"


def validate_windows_partition_size(instance_id, drive_letter, size, volume_id):
    try:
        MAX_RETRIALS_NUM = 3
        WAITING_STATUS = ["Pending", "InProgress", "Delayed"]
        size = int(size)
        resp = ssm_client.send_command(
            InstanceIds=[instance_id],
            DocumentName="AWS-RunPowerShellScript",
            TimeoutSeconds=500,
            Parameters={
                "commands": [
                    '$volId = $null',
                    '$volId = ($volume_id.Split("-") | Select -Index 0,1) -join ""',
                    "$partitionType = get-disk | Where-Object SerialNumber -match $volId | Select -ExpandProperty PartitionStyle -first 1",
                    "if(($partitionType -eq 'MBR') -and (($size * 1024 * 1024 * 1024 ) -gt (2 * 1024 * 1024 * 1024 * 1024)))",
                    "{exit 1}",
                ]
            },
        )
        cmd_id = resp["Command"]["CommandId"]
        status, status_details = get_command_status_with_wait(
            instance_id, cmd_id
        )

        for retries in range(MAX_RETRIALS_NUM):
            if status in WAITING_STATUS:
                time.sleep(10)
                status, status_details = get_command_status_with_wait(
                    instance_id, cmd_id
                )

        if status_details == "Failed":
            raise Exception(
                "The target size is greater than the max size of the partition"
            )
    except Exception as e:
        raise Exception(e)
    return True


def get_command_status_with_wait(instance_id, command_id):
    time.sleep(10)
    response = ssm_client.get_command_invocation(
        CommandId=command_id, InstanceId=instance_id
    )
    status = response["Status"]
    details = response["StatusDetails"]
    return status, details
