import boto3

def get_alb_attributes(load_balancer_arn):
  elbv2_client = boto3.client("elbv2")
  response = elbv2_client.describe_load_balancer_attributes(
    LoadBalancerArn=load_balancer_arn
  )
  return response["Attributes"]

def handler(event, context):
  load_balancer_arn = event["LoadBalancerArn"]
  desync_mitigation_mode = event["DesyncMitigationMode"]

  alb_attributes = get_alb_attributes(load_balancer_arn)

  attribute_modified = False
  for attribute in alb_attributes:
    mode_string = "routing.http.desync_mitigation_mode"
    if attribute["Key"] == mode_string and attribute["Value"] == desync_mitigation_mode:
      attribute_modified = True
      break

  if attribute_modified == False:
    raise ValueError(f"VERIFICATION FAILED. DESYNC MITIGATION MODE MODIFICATION TO {desync_mitigation_mode} WAS UNSUCCESSFUL.")

  return {"ModificationResult": f"Verification of Desync Mitigation Mode was successfully modified to be {desync_mitigation_mode}."}
