import boto3

def validate_s3_bucket_publicaccessblock(event, context):
    s3_client      = boto3.client("s3")
    bucket         = event["Bucket"]
    bucket_pabs    = s3_client.get_public_access_block(Bucket=bucket)["PublicAccessBlockConfiguration"]
    all_pabs       = ["BlockPublicAcls", "IgnorePublicAcls", "BlockPublicPolicy", "RestrictPublicBuckets"]
    unmatched_pabs = [pab for pab in all_pabs if bucket_pabs[pab] != event[pab]]

    if unmatched_pabs:
        info = "CONFIGURATION VALUES DO NOT MATCH WITH PARAMETERS PROVIDED VALUES "
        for pab in unmatched_pabs:
            info += f"{pab}: {bucket_pabs[pab]}, "
        info.rstrip(", ")
        raise Exception(info)

    return {
        "output": {
            "message": "Bucket public access block configuration successfully set.",
            "configuration": bucket_pabs
        }
    }
