inputs.TemplateBody:
{
  "Parameters": {
      "ExecutionId": {
        "Description": "Used to create a unique S3 bucket.",
        "Type": "String"
      }
  },
  "Resources": {
    "S3Bucket": {
        "Type": "AWS::S3::Bucket",
        "Properties": {
          "BucketName": {
            "Fn::Join": ["", ["s3staticwebsite", {"Ref":"ExecutionId"}]]
          },
          "AccessControl": "PublicRead",
          "WebsiteConfiguration": {
            "IndexDocument": "index.html",
            "ErrorDocument": "error.html"
          }
        }
    },
    "BucketPolicy": {
      "Type": "AWS::S3::BucketPolicy",
      "Properties": {
        "PolicyDocument": {
          "Id": "MyPolicy",
          "Statement": [
              {
                "Sid": "PublicReadForGetBucketObjects",
                "Effect": "Allow",
                "Principal": "*",
                "Action": "s3:GetObject",
                "Resource": {
                  "Fn::Join": [
                      "",
                      [
                        "arn:aws:s3:::",
                        {
                            "Ref": "S3Bucket"
                        },
                        "/*"
                      ]
                    ]
                }
              }
            ]
        },
        "Bucket": {
          "Ref": "S3Bucket"
        }
      }
    }
  },
  "Outputs": {
    "S3BucketName": {
      "Value": {
        "Ref": "S3Bucket"
      },
      "Description": "Name of S3Bucket"
    }
  }
}
