runCommand:
$returnValue = 0
$timeBetweenConnectionsInSeconds = 30
$getTestNetConnectionScriptBlock = {
    Param ($ip_and_port)

    # Handle IPv6 format
    if ($ip_and_port -match '^\[(.*)\]:(.*)$') {
        # IPv6 format: [ip]:port
        $ip = $matches[1]
        $port = $matches[2]
    } else {
        # IPv4 format: ip:port
        $ip, $port = $ip_and_port.Split(":")
    }

    try {
        $res = Test-NetConnection -ComputerName "$ip" -Port "$port" -WarningAction SilentlyContinue
        $res.TcpTestSucceeded
    }
    catch {
        Write-Host "An error occured during the connectivity check"
        $False
    }
}

'{{HostsAndPorts}}'.split(' ') | foreach {
    $startTime = Get-Date
    $testNetConnectionJob = Start-Job -ScriptBlock $getTestNetConnectionScriptBlock -ArgumentList "$_" -Name "$_"
    Wait-Job $testNetConnectionJob -Timeout $timeBetweenConnectionsInSeconds | Out-Null
    Stop-Job $testNetConnectionJob.id
    Wait-Job $testNetConnectionJob.id | Out-Null

    $jobDetails = Get-Job $testNetConnectionJob.id
    if ("$($jobDetails.State)" -eq "Completed") {
        $res = Receive-Job $testNetConnectionJob.id
        if ($res) {
            Write-Host $jobDetails.Name, "connection succeeded"
        }
        else {
            Write-Host $jobDetails.Name, "connection failed"
            $returnValue = 1
        }
    } elseif ("$($jobDetails.State)" -eq "Stopped") {
        Write-Host $jobDetails.Name, "connection timeout"
        $returnValue = 1
    } else {
        Write-Host $jobDetails.Name, "unexpected error"
        $returnValue = 1
    }
    $secondsUntilNextTest = $timeBetweenConnectionsInSeconds - [Int]((Get-Date) - $startTime).TotalSeconds
    if ($secondsUntilNextTest -gt 0) {
        Start-Sleep -Seconds $secondsUntilNextTest
    }
}

exit $returnValue
