runCommand:
class DiskInfo {
    [string]$DiskNumber
    [int64]$Size
    [bool]$IsReadOnly
    [bool]$IsOnline

    DiskInfo([string]$diskNumber, [int64]$size, [bool]$IsReadOnly, [bool]$IsOnline) {
        $this.DiskNumber = $diskNumber
        $this.Size = $size
        $this.IsReadOnly = $IsReadOnly
        $this.IsOnline = $IsOnline
    }

    # Method to display disk info
    [void]DisplayDiskInfo() {
        Write-Host "Disk Number: $($this.DiskNumber)"
        Write-Host "Disk Size: $($this.Size)"
        Write-Host "IsReadOnly: $($this.IsReadOnly)"
        Write-Host "IsOnline: $($this.IsOnline)"
    }
}


function Get-DisksProperties {
    $physicaldisks = Get-PhysicalDisk | Select-Object DeviceID, Size, OperationalStatus
    $deviceToSize = @{}

    foreach ($disk in $physicaldisks) {
        $deviceToSize[$disk.DeviceID] = @{
            Size = $disk.Size
            IsOnline = ($disk.OperationalStatus -eq "OK")
        }
    }

    return $deviceToSize
}


function Is-DiskReadOnly {
    param(
        [Parameter(Mandatory=$true)]
        [int]$diskNumber
    )

    $diskAttrScript = @"
 select disk $diskNumber
 attributes disk
"@
    $diskAttrOutput = Invoke-Expression -Command "echo '$diskAttrScript' | diskpart"
    $diskAttrOutputLines = $diskAttrOutput -split "\n"

    if ($diskAttrOutputLines -match "Read-only\s+:\s+Yes.*") {
        return $true
    }
    return $false
}

function Get-DiskInfos {
    $devicesProperties = Get-DisksProperties

    $diskList = New-Object 'System.Collections.Generic.List[DiskInfo]'
    foreach ($deviceNumber in $devicesProperties.Keys) {
        $isReadOnly = Is-DiskReadOnly -diskNumber $deviceNumber
        $deviceSize = $devicesProperties[$deviceNumber].Size
        $isOnline = $devicesProperties[$deviceNumber].IsOnline

        $diskList.Add([DiskInfo]::new($deviceNumber, $deviceSize, $isReadOnly, $isOnline))
    }
    return $diskList
}


function NormalizeSize {
    param($sizeBytes)

    return [Math]::Ceiling($sizeBytes / 1GB)
}

try {
    $userDisks = ConvertFrom-Json '{{Volumes}}'
}

catch {
    Write-Host "Error: failed to parse Volumes:"
    exit 1
}

$systemDisks = Get-DiskInfos

if ($systemDisks.Count -ne $userDisks.Count) {
    Write-Host "Error: found #$(@($systemDisks).Count) disks, expected #$(@($userDisks).Count)"
    exit 1
}

$ret = 0

foreach ($userDisk in $userDisks) {

    $systemDisk = $null

    for ($i=0; $i -lt $systemDisks.Count; $i++) {
        if ("$(NormalizeSize $userDisk.totalStorageBytes)" -eq "$(NormalizeSize $systemDisks[$i].Size)") {
            $systemDisk = $systemDisks[$i]
            $systemDisks = $systemDisks | Where-Object { $_.DiskNumber â€“ne $systemDisks[$i].DiskNumber }
            break
        }
    }

    if ($systemDisk -eq $null) {
        Write-Host "Error: disk $($userDisk.deviceName) is not found"
        $ret = 1
    }

    else {
        if ($systemDisk.isOffline) {
            Write-Host "Error: disk $($userDisk.deviceName) is offline"
            $ret = 1
        }

        if ($systemDisk.isReadOnly) {
            Write-Host "Error: disk $($userDisk.deviceName) is read_only"
            $ret = 1
        }

        if (-not ($systemDisk.isOffline -or $systemDisk.isReadOnly)) {
            Write-Host "Success: disk $($userDisk.deviceName) is found"
        }
    }
}

exit $ret
