def script_handler(events, context):
    desc_tags = events['InstanceDescription'].get('Tags', [])
    instance_tags = {tag['Key']: tag['Value'] for tag in desc_tags}
    tags_to_verify = events['TagsToVerify'].split(',')

    res = {
      'succeeded_tags': [],
      'failed_tags': []
    }

    for tag in tags_to_verify:
        key_val = tag.split('=')
        key = key_val[0]
        val = key_val[1] if len(key_val) == 2 else ''

        if key in instance_tags:
            if not val:
                res['succeeded_tags'].append(f"{key}: Ignoring value") 
            elif instance_tags[key] == val:
                res['succeeded_tags'].append(f"{key}: Value '{val}' ok")
            else:
                res['failed_tags'].append(f"{key},'{val}': Not found")
        else:
            res['failed_tags'].append(f"{key},'{val}': Not found")

    return res

def script_handler(events, context):
    if events["FailedTags"]:
        raise Exception("Tag verification failed. One or more tags are missing or do not match. See output for details.")
