import boto3
import time
import urllib.request

TEMPLATE_SOURCE_S3 = 'S3'
TEMPLATE_SOURCE_GITHUB = 'GitHub'
TEMPLATE_SOURCE_BODY = 'TemplateBody'


def validate_github_url(url):
    if not url.startswith('https://'):
        raise Exception('GitHub source type only supports downloads via HTTPS')
    if not url.startswith('https://github.com/') and not url.startswith('https://raw.githubusercontent.com/'):
        raise Exception(
            'Provide a URL that corresponds to a path within a GitHub repository')


def download_from_https(url):
    with urllib.request.urlopen(url) as resp:
        template_content = resp.read().decode('utf-8')
    return template_content


def put_organization_conformance_pack_with_retry(config_client, cpack_params, retries=5, delay=30):
    try:
        return {
            'ConformancePackArn': config_client.put_organization_conformance_pack(**cpack_params)['OrganizationConformancePackArn']
        }
    except config_client.exceptions.ResourceInUseException as error:
        if retries > 0:
            time.sleep(delay)
            return put_organization_conformance_pack_with_retry(config_client, cpack_params, retries - 1)
        else:
            raise error


def put_conformance_pack_handler(events, context):
    # get required parameters
    deploy_to_organization = events['DeployToOrganization']
    conformance_pack_name = events['ConformancePackName']
    template_source = events['TemplateSource']
    conformance_pack_input_parameters = events['ConformancePackInputParameters']
    excluded_accounts = events['ExcludedAccounts']

    # verify that accounts to exclude are used only when deploying organization CPacks
    if excluded_accounts and not deploy_to_organization:
        raise Exception(
            'You can\'t specify ExcludedAccounts when deploying a local conformance pack.')

    # default conformance pack parameters
    cpack_params = {
        'ConformancePackInputParameters': conformance_pack_input_parameters
    }

    # add conformance pack definition
    template_source_type = template_source['Type']
    template_source_value = template_source['Value']

    if template_source_type == TEMPLATE_SOURCE_S3:
        cpack_params['TemplateS3Uri'] = template_source_value
    elif template_source_type == TEMPLATE_SOURCE_BODY:
        cpack_params['TemplateBody'] = template_source_value
    elif template_source_type == TEMPLATE_SOURCE_GITHUB:
        validate_github_url(template_source_value)
        cpack_params['TemplateBody'] = download_from_https(
            template_source_value)
    else:
        raise Exception(
            f'The conformance pack template source must be one of {TEMPLATE_SOURCE_S3}, {TEMPLATE_SOURCE_BODY}, {TEMPLATE_SOURCE_GITHUB}')

    # deploy conformance pack
    config_client = boto3.client('config')
    if deploy_to_organization:
        cpack_params['OrganizationConformancePackName'] = conformance_pack_name
        cpack_params['ExcludedAccounts'] = excluded_accounts
        return put_organization_conformance_pack_with_retry(config_client, cpack_params)
    else:
        cpack_params['ConformancePackName'] = conformance_pack_name
        return {
            'ConformancePackArn': config_client.put_conformance_pack(**cpack_params)['ConformancePackArn']
        }

import boto3


def describe_conformance_pack_status_handler(events, context):
    deploy_to_organization = events['DeployToOrganization']
    config_client = boto3.client('config')
    conformance_pack_name = events['ConformancePackName']
    if deploy_to_organization:
        status = config_client.describe_organization_conformance_pack_statuses(OrganizationConformancePackNames=[conformance_pack_name])['OrganizationConformancePackStatuses'][0]
        if status['Status'] != "CREATE_SUCCESSFUL" and status['Status'] != "UPDATE_SUCCESSFUL":
            raise Exception(status['ErrorMessage'])
    else:
        status = config_client.describe_conformance_pack_status(ConformancePackNames=[conformance_pack_name])['ConformancePackStatusDetails'][0]
        if status['ConformancePackState'] != "CREATE_COMPLETE":
            raise Exception(status['ConformancePackStatusReason'])
