import boto3
from botocore.exceptions import ClientError

resource_explorer = boto3.client('resource-explorer-2')

tags = {
    'Type': 'QuickSetup'
}

def script_handler(events, context):
    print('Creating an Index...')
    try:
        resource_explorer.create_index(
            Tags=tags
        )
        print('Index created successfully')
    except ClientError as error:
        if error.response['Error']['Code'] == 'ConflictException':
            print('No-op; an Index already exists')
            return
        print(f'Failed to create an Index: {error}')
        raise error

import boto3
from botocore.exceptions import ClientError

resource_explorer = boto3.client('resource-explorer-2')

promote = {
    'toPromote': True
}

notPromote = {
    'toPromote': False
}

def get_aggregator_index():
    try:
        paginator = resource_explorer.get_paginator('list_indexes')
        response_iterator = paginator.paginate(
            Type='AGGREGATOR',
        )
    except ClientError as error:
        print(f'Failed to list Aggregator Index: {error}')
        raise error

    aggregator_index = []
    for page in response_iterator:
        aggregator_index += page['Indexes']

    return aggregator_index[0] if aggregator_index else {}


def script_handler(events, context):
    current_region = events['AWS_REGION']
    selected_aggregator_region = events['aggregatorRegion']
    replace_existing_aggregator = events['replaceExistingAggregator']

    # Is the current Region the same as the selected Aggregator Region?
    if current_region != selected_aggregator_region:
        print(
            f'The current Region ({current_region}) is not the same as the selected Aggregator Region ({selected_aggregator_region}). Skipping promotion...')
        return notPromote

    aggregator_index = get_aggregator_index()

    # Did the user choose to replace the existing Aggregator Index?
    if replace_existing_aggregator == 'false':
        print('The user chose not to replace the existing Aggregator Index')
        # Does an Aggregator Index already exist in any Region?
        if aggregator_index:
            aggregator_index_region = aggregator_index.get('Region')
            print(
                f'An Aggregator Index already exists in {aggregator_index_region}. Skipping promotion...')
            return notPromote
        print(
            'An Aggregator Index does not exist in any Region. Proceeding to promotion...')
        return promote
    else:
        print('The user chose to replace the existing Aggregator Index')
        # Is there an existing Aggregator Index, and is it in the selected/current region?
        if aggregator_index.get('Region') == current_region:
            print(
                'The existing Index in the selected Aggregator Region is already an Aggregator Index. Skipping promotion...')
            return notPromote
        print('The existing Index in the selected Aggregator Region is not an Aggregator Index. Proceeding to promotion...')
        return promote

import boto3
import time
from botocore.exceptions import ClientError

resource_explorer = boto3.client('resource-explorer-2')

sleep = {
    'toSleep': True
}

notSleep = {
    'toSleep': False
}


def get_index_arn():
    print('Getting the Arn of the Index in the current Region...')
    try:
        response = resource_explorer.get_index()
        index_arn = response['Arn']
        print(f'The Index Arn is {index_arn}')
        return index_arn
    except ClientError as error:
        print(f'Failed to get the Index Arn: {error}')
        raise error


def promote_index(index_arn):
    print(f'Promoting the Index with the Arn {index_arn} to Aggregator...')
    try:
        resource_explorer.update_index_type(
            Arn=index_arn,
            Type='AGGREGATOR'
        )

        # Wait for Index to get into ACTIVE State for up to 18 * 30 (540) seconds, or 9 minutes
        counter = 0
        while counter < 18:
            index_state = resource_explorer.get_index()['State']
            if index_state == 'ACTIVE':
                print('Index Promotion complete!')
                return notSleep
            counter += 1
            time.sleep(30)
        raise AssertionError(
            'Timed out while waiting for the promotion to complete.')
    except ClientError as error:
        if error.response['Error']['Code'] != 'ServiceQuotaExceededException':
            print(f'Failed to promote the Index to Aggregator: {error}')
            raise error
        return sleep


def script_handler(events, context):
    index_arn = get_index_arn()
    sleep_action = promote_index(index_arn)
    return sleep_action

import boto3
from botocore.exceptions import ClientError

VIEWNAME = 'all-resources'
TAGS = {
    'Type': 'QuickSetup'
}


def create_qs_view(aggregator_region):
    '''
    If successful, returns view_arn
    '''
    print(f'Creating QuickSetup View named {VIEWNAME}...')
    regional_resource_explorer = boto3.client(
        'resource-explorer-2', region_name=aggregator_region
    )

    try:
        response = regional_resource_explorer.create_view(
            Tags=TAGS,
            ViewName=VIEWNAME,
            IncludedProperties=[
                {
                    'Name': 'tags'
                },
            ]
        )
    except ClientError as error:
        print(
            f'Failed to create QuickSetup View named {VIEWNAME} in aggregator region {aggregator_region}: {error}')
        raise error

    view_arn = response['View']['ViewArn']
    print(
        f'QuickSetup View with ViewArn {view_arn} created in aggregator region {aggregator_region}')
    return view_arn


def get_aggregator_index():
    re_client = boto3.client('resource-explorer-2')
    try:
        paginator = re_client.get_paginator('list_indexes')
        response_iterator = paginator.paginate(
            Type='AGGREGATOR',
        )
    except ClientError as error:
        print(f'Failed to list Aggregator Index: {error}')
        raise error

    aggregator_index = []
    for page in response_iterator:
        aggregator_index += page['Indexes']

    return aggregator_index[0] if aggregator_index else aggregator_index


def get_existing_qs_view_arn(existing_views):
    for view_arn in existing_views:
        if view_arn.find(f'/{VIEWNAME}/') >= 0:
            return view_arn


def get_views_in_region(aggregator_region):
    try:
        regional_resource_explorer = boto3.client(
            'resource-explorer-2', region_name=aggregator_region
        )
        views = []
        paginator = regional_resource_explorer.get_paginator('list_views')
        response_iterator = paginator.paginate()
        for page in response_iterator:
            views += page['Views']
        return views
    except ClientError as error:
        print(f'Failed to list Views: {error}')
        raise error


def set_default_view(view_arn, aggregator_region):
    print(f'Setting QuickSetup View named {VIEWNAME} as default...')
    regional_resource_explorer = boto3.client(
        'resource-explorer-2', region_name=aggregator_region
    )
    try:
        regional_resource_explorer.associate_default_view(
            ViewArn=view_arn
        )
        print('QuickSetup View set as default successfully!')

    except ClientError as error:
        print(
            f'Failed to set QuickSetup View named {VIEWNAME} as default: {error}')
        raise error


def get_default_view_arn(aggregator_region):
    try:
        regional_resource_explorer = boto3.client(
            'resource-explorer-2', region_name=aggregator_region
        )
        default_view_arn = regional_resource_explorer.get_default_view().get('ViewArn')
        return default_view_arn
    except ClientError as error:
        print(f'Failed to retrieve default View: {error}')
        raise error


def handle_existing_views(existing_views, aggregator_region):
    default_view_arn = get_default_view_arn(aggregator_region)
    if default_view_arn:
        print(
            f'No-Op: Default view {default_view_arn} already exists in {aggregator_region}')
        return

    try:
        qs_view_arn = create_qs_view(aggregator_region)
        set_default_view(qs_view_arn, aggregator_region)
    except ClientError as error:
        if error.response['Error']['Code'] == 'ConflictException':
            existing_qs_view_arn = get_existing_qs_view_arn(existing_views)
            set_default_view(existing_qs_view_arn, aggregator_region)

        if error.response['Error']['Code'] == 'ServiceQuotaExceededException':
            existing_qs_view_arn = get_existing_qs_view_arn(existing_views)
            if existing_qs_view_arn:
                set_default_view(existing_qs_view_arn, aggregator_region)


def handle_no_existing_views(aggregator_region):
    view_arn = create_qs_view(aggregator_region)
    set_default_view(view_arn, aggregator_region)


def script_handler(events, context):
    aggregator_index = get_aggregator_index()
    if not aggregator_index:
        raise ValueError('Aggregator Index does not exist in this account')

    aggregator_region = aggregator_index['Region']

    existing_views = get_views_in_region(aggregator_region)
    if existing_views:
        handle_existing_views(existing_views, aggregator_region)
    else:
        handle_no_existing_views(aggregator_region)
