import boto3

ALL_AWS_RESOURCES = 'AWS::ALL'
CONFIG_SLR_NAME = 'AWSServiceRoleForConfig'
CONFIG_SERVICE_PRINCIPAL = 'config.amazonaws.com'
RECORDER_UPDATE_STRATEGY_FAIL = "FAIL"
RECORDER_UPDATE_STRATEGY_NO_OP = "NO_OP"
RECORDER_UPDATE_STRATEGY_MERGE = "MERGE"
RECORDER_UPDATE_STRATEGY_OVERWRITE = "OVERWRITE"


def put_configuration_recorder_handler(events, context):
    # document parameters
    record_resource_types = events['ResourceTypes']
    record_global_resource_types = events['RecordGlobalResourceTypes']
    global_resource_types_region = events['GlobalResourceTypesRegion']
    recorder_update_strategy = events['RecorderUpdateStrategy']

    should_record_global_resources = record_global_resource_types and context['global:REGION'] == global_resource_types_region
    print('Should enable recording of global resources in this region: ' + str(should_record_global_resources))

    # fetch current recorders
    config_client = boto3.client('config')
    current_recorders = config_client.describe_configuration_recorders()[
        'ConfigurationRecorders']

    # create/update configuration recorder
    recorder_name = None
    if not current_recorders:
        print('Creating a new configuration recorder.')
        validate_resources_to_record(
            record_resource_types, should_record_global_resources)
        recorder_name = set_configuration_recorder(
            client=config_client,
            recorder=None,
            params={
                'roleARN': get_recorder_role(),
                'resourceTypes': record_resource_types,
                'recordGlobalResources': should_record_global_resources
            }
        )
    elif recorder_update_strategy == RECORDER_UPDATE_STRATEGY_MERGE:
        print('Extending existing configuration recorder.')
        recorder_name = extend_configuration_recorder(
            client=config_client,
            recorder=current_recorders[0],
            params={
                'roleARN': get_recorder_role(current_recorders[0]),
                'resourceTypes': record_resource_types,
                'recordGlobalResources': should_record_global_resources
            }
        )
    elif recorder_update_strategy == RECORDER_UPDATE_STRATEGY_OVERWRITE:
        print('Overwriting existing configuration recorder.')
        validate_resources_to_record(
            record_resource_types, should_record_global_resources)
        recorder_name = set_configuration_recorder(
            client=config_client,
            recorder=current_recorders[0],
            params={
                'roleARN': get_recorder_role(current_recorders[0]),
                'resourceTypes': record_resource_types,
                'recordGlobalResources': should_record_global_resources
            }
        )
    elif recorder_update_strategy == RECORDER_UPDATE_STRATEGY_NO_OP:
        print(f'Skipping the update of the configuration recorder as the RecorderUpdateStrategy is set to "{recorder_update_strategy}"')
        recorder_name = current_recorders[0]['name']
    elif recorder_update_strategy == RECORDER_UPDATE_STRATEGY_FAIL:
        raise Exception(f'Failing the step execution, since an existing recorder is detected and the RecorderUpdateStrategy is set to "{recorder_update_strategy}"')
    else:
        raise Exception(f'Unrecognized RecorderUpdateStrategy value: {recorder_update_strategy}')

    return {
        'ConfigurationRecorderName': recorder_name
    }


def validate_resources_to_record(record_resource_types, should_record_global_resources):
    if ALL_AWS_RESOURCES in record_resource_types:
        if len(record_resource_types) > 1:
            raise Exception(
                'ResourceTypes must be AWS::ALL or must contain a list of AWS resources (for example, AWS::EC2::Instance or AWS::CloudTrail::Trail), but not both.')
    else:
        if should_record_global_resources:
            raise Exception(
                'To record global resource types, RecordResourceType must be set to AWS::ALL, RecordGlobalResourceTypes must be enabled and GlobalResourceTypesRegion must be set to a region.')

        if not record_resource_types:
            raise Exception(
                'Specify the AWS resources for which Config recording should be enabled.')


def get_recorder_role(recorder=None):
    iam_client = boto3.client('iam')
    if recorder:
        return recorder['roleARN']
    else:
        try:
            config_slr = iam_client.get_role(
                RoleName=CONFIG_SLR_NAME
            )
            return config_slr['Role']['Arn']
        except iam_client.exceptions.NoSuchEntityException:
            return iam_client.create_service_linked_role(
                AWSServiceName=CONFIG_SERVICE_PRINCIPAL
            )['Role']['Arn']


def set_configuration_recorder(client, recorder, params):
    recorder_settings = {
        'roleARN': params['roleARN'],
        'recordingGroup': {
            'allSupported': ALL_AWS_RESOURCES in params['resourceTypes'],
            'includeGlobalResourceTypes': params['recordGlobalResources'],
            'resourceTypes': [] if ALL_AWS_RESOURCES in params['resourceTypes'] else params['resourceTypes']
        }
    }
    recorder_settings['name'] = recorder['name'] if recorder else 'default'

    client.put_configuration_recorder(
        ConfigurationRecorder=recorder_settings
    )
    return recorder_settings['name']


def extend_configuration_recorder(client, recorder, params):
    recorder_name = recorder['name']
    recorder_role = params['roleARN']
    recording_group = recorder['recordingGroup']

    record_global_resources = recording_group['includeGlobalResourceTypes'] or params['recordGlobalResources']
    record_all_resources = recording_group['allSupported'] or ALL_AWS_RESOURCES in params[
        'resourceTypes'] or record_global_resources
    resource_types = [] if record_all_resources else list(
        set(recording_group['resourceTypes']) | set(params['resourceTypes']))

    client.put_configuration_recorder(
        ConfigurationRecorder={
            'name': recorder_name,
            'roleARN': recorder_role,
            'recordingGroup': {
                'allSupported': record_all_resources,
                'includeGlobalResourceTypes': record_global_resources,
                'resourceTypes': resource_types
            }
        }
    )

    return recorder_name

import boto3
import botocore
import json
import time

from botocore.utils import ArnParser

CHANNEL_UPDATE_STRATEGY_FAIL = 'FAIL'
CHANNEL_UPDATE_STRATEGY_NO_OP = 'NO_OP'
CHANNEL_UPDATE_STRATEGY_OVERWRITE = 'OVERWRITE'
DEFAULT_DELIVERY_TOPIC_NAME = 'ConfigRecording-Default-Topic'


def get_partition(region):
    if region.startswith('cn-'):
        return 'aws-cn'
    elif region.startswith('us-gov-'):
        return 'aws-us-gov'
    else:
        return 'aws'

def get_bucket_policy(bucket_name, bucket_prefix, account, region):
    bucket_arn = f'arn:{get_partition(region)}:s3:::{bucket_name}'
    return {
        'Statement': [
            {
                'Sid': 'AWSConfigBucketPermissionsCheck',
                'Effect': 'Allow',
                'Principal': {
                    'Service': 'config.amazonaws.com'
                },
                'Action': 's3:GetBucketAcl',
                'Resource': bucket_arn,
                'Condition': { 
                    'StringEquals': {
                      'AWS:SourceAccount': account
                    }
                }
            },
            {
                'Sid': 'AWSConfigBucketExistenceCheck',
                'Effect': 'Allow',
                'Principal': {
                    'Service': 'config.amazonaws.com'
                },
                'Action': 's3:ListBucket',
                'Resource': bucket_arn,
                'Condition': { 
                    'StringEquals': {
                      'AWS:SourceAccount': account
                    }
                }
            },
            {
                'Sid': 'AWSConfigBucketDelivery',
                'Effect': 'Allow',
                'Principal': {
                    'Service': 'config.amazonaws.com'
                },
                'Action': 's3:PutObject',
                'Resource': f'{bucket_arn}/{bucket_prefix}/AWSLogs/{account}/Config/*' if bucket_prefix else f'{bucket_arn}/AWSLogs/{account}/Config/*',
                'Condition': {
                    'StringEquals': {
                        's3:x-amz-acl': 'bucket-owner-full-control',
                        'AWS:SourceAccount': account
                    }
                }
            }
        ]
    }

def create_delivery_bucket(bucket_name, bucket_prefix, account, region, automation_role_arn):
    s3_client = boto3.client('s3')
    bucket_policy = get_bucket_policy(bucket_name, bucket_prefix, account, region)

    bucket_params = {
        'Bucket': bucket_name
    }
    if region != 'us-east-1':
        bucket_params['CreateBucketConfiguration'] = {
            'LocationConstraint': region
        }

    s3_client.create_bucket(**bucket_params)
    s3_client.put_bucket_policy(
        Bucket=bucket_name,
        Policy=json.dumps(bucket_policy)
    )
    s3_client.put_bucket_encryption(
        Bucket=bucket_name,
        ServerSideEncryptionConfiguration={
            'Rules': [
                {
                    'ApplyServerSideEncryptionByDefault': {
                        'SSEAlgorithm': 'AES256'
                    }
                },
            ]
        }
    )

    # Turn on all 4 public access blocks
    s3_client.put_public_access_block(
        Bucket=bucket_name,
        PublicAccessBlockConfiguration={
            'BlockPublicAcls': True,
            'IgnorePublicAcls': True,
            'BlockPublicPolicy': True,
            'RestrictPublicBuckets': True
        }
    )

def verify_delivery_bucket_exists(bucket_name, bucket_prefix, account, region, automation_role_arn, retries=10, delay=5):
    try:
        s3_client = boto3.client('s3')
        s3_client.head_bucket(
            Bucket=bucket_name
        )
        # if bucket exists always update bucket policy for qs owned buckets because of potential security issue
        if bucket_name.startswith('aws-quick-setup-config-recording'):
            s3_client.put_bucket_policy(
                Bucket=bucket_name,
                Policy=json.dumps(get_bucket_policy(bucket_name, bucket_prefix, account, region))
            )
    except botocore.exceptions.ClientError as error:
        if error.response['Error']['Code'] == '403':
            pass
        elif error.response['Error']['Code'] == '404' and retries > 0:
            try:
                create_delivery_bucket(
                    bucket_name, bucket_prefix, account, region, automation_role_arn)
            except botocore.exceptions.ClientError as err:
                if err.response['Error']['Code'] == 'BucketAlreadyOwnedByYou':
                    pass
                elif err.response['Error']['Code'] == 'OperationAborted':
                    time.sleep(delay)
                    verify_delivery_bucket_exists(
                        bucket_name, bucket_prefix, account, region, retries - 1)
                else:
                    raise err
        else:
            raise error


def get_default_topic_arn(account, region):
    sns_client = boto3.resource('sns')
    existing_topics = dict(map(lambda topic: [topic.arn.rsplit(':', 1)[
                           1], topic.arn], sns_client.topics.all()))

    if DEFAULT_DELIVERY_TOPIC_NAME not in existing_topics:
        expected_topic_arn = f'arn:{get_partition(region)}:sns:{region}:{account}:ConfigRecording-Default-Topic'
        topic = sns_client.create_topic(
            Name=DEFAULT_DELIVERY_TOPIC_NAME,
            Attributes={
                'DisplayName': DEFAULT_DELIVERY_TOPIC_NAME,
                'Policy': json.dumps({
                    'Statement': [
                        {
                            'Sid': '__default_statement_ID',
                            'Effect': 'Allow',
                            'Principal': {
                                'AWS': '*'
                            },
                            'Action': [
                                'sns:GetTopicAttributes',
                                'sns:SetTopicAttributes',
                                'sns:AddPermission',
                                'sns:RemovePermission',
                                'sns:DeleteTopic',
                                'sns:Subscribe',
                                'sns:ListSubscriptionsByTopic',
                                'sns:Publish'
                            ],
                            'Resource': expected_topic_arn,
                            'Condition': {
                                'StringEquals': {
                                    'AWS:SourceOwner': account
                                }
                            }
                        },
                        {
                            'Sid': 'AWSConfigSNSPolicy',
                            'Effect': 'Allow',
                            'Principal': {
                                'Service': 'config.amazonaws.com'
                            },
                            'Action': 'sns:Publish',
                            'Resource': expected_topic_arn
                        }
                    ]
                })
            }
        )
        return topic.arn
    else:
        return existing_topics[DEFAULT_DELIVERY_TOPIC_NAME]


def get_delivery_channel_settings(current_settings, params, region):
    name = current_settings.get(
        'name', '') or 'aws-quick-setup-delivery-channel'
    s3_bucket_name = params['DeliveryChannelS3BucketName'] or current_settings.get(
        's3BucketName', '')
    s3_key_prefix = params['DeliveryChannelS3KeyPrefix']
    stream_to_sns = params['StreamConfigurationChangesToSNS']
    sns_topic_name = params['DeliveryChannelSNSTopicName']
    sns_topic_account_id = params['DeliveryChannelSNSTopicAccountID']
    delivery_frequency = params['DeliveryChannelDeliveryFrequency']

    channel_settings = {
        'name': name,
        's3BucketName': s3_bucket_name
    }
    if s3_key_prefix:
        channel_settings['s3KeyPrefix'] = s3_key_prefix
    if stream_to_sns and sns_topic_name and sns_topic_account_id:
        channel_settings['snsTopicARN'] = "arn:{}:sns:{}:{}:{}".format(get_partition(region), region, sns_topic_account_id, sns_topic_name)
    if delivery_frequency:
        channel_settings['configSnapshotDeliveryProperties'] = {
            'deliveryFrequency': delivery_frequency
        }

    return channel_settings


def put_delivery_channel_handler(events, context):
    channel_update_strategy = events['DeliveryChannelUpdateStrategy']
    stream_to_sns = events['StreamConfigurationChangesToSNS']
    account = context['global:ACCOUNT_ID']
    region = context['global:REGION']
    automation_role_arn = events['AutomationAssumeRoleArn']

    config_client = boto3.client('config')
    current_delivery_channels = config_client.describe_delivery_channels()[
        'DeliveryChannels']

    delivery_channel_name = ''
    # we are always updating the QS-managed delivery channel
    if not current_delivery_channels or current_delivery_channels[0]['name'] == 'aws-quick-setup-delivery-channel' or channel_update_strategy == CHANNEL_UPDATE_STRATEGY_OVERWRITE:
        current_settings = current_delivery_channels[0] if current_delivery_channels else {
        }
        new_channel_settings = get_delivery_channel_settings(
            current_settings, events, region)

        if 's3BucketName' in new_channel_settings:
            verify_delivery_bucket_exists(
                new_channel_settings['s3BucketName'],
                new_channel_settings.get('s3KeyPrefix', ''),
                account,
                region,
                automation_role_arn
            )

        if stream_to_sns and 'snsTopicARN' not in new_channel_settings:
            new_channel_settings['snsTopicARN'] = get_default_topic_arn(
                account, region)

        config_client.put_delivery_channel(
            DeliveryChannel=new_channel_settings
        )
        delivery_channel_name = new_channel_settings['name']
    elif channel_update_strategy == CHANNEL_UPDATE_STRATEGY_FAIL:
        raise Exception(
            f'Failing the step execution, since an existing delivery channel is detected and the DeliveryChannelUpdateStrategy is set to "{channel_update_strategy}"')
    elif channel_update_strategy == CHANNEL_UPDATE_STRATEGY_NO_OP:
        delivery_channel_name = current_delivery_channels[0]['name']
        print(
            f'Skipping the update of the configuration recorder as the DeliveryChannelUpdateStrategy is set to "{channel_update_strategy}"')
    else:
        raise Exception(
            f'Unrecognized DeliveryChannelUpdateStrategy value: {channel_update_strategy}')

    return {
        'DeliveryChannelName': delivery_channel_name
    }
