import boto3
import json
import os
import cfnresponse
from botocore.exceptions import ClientError

import logging

logging.basicConfig(level=logging.INFO)
logger = logging.getLogger(__name__)
logger.setLevel(logging.INFO)

# Events sent by CloudFormation
CREATE = 'Create'
UPDATE = 'Update'
DELETE = 'Delete'

session = boto3.session.Session()
region = session.region_name
partition = session.get_partition_for_region(region)
iam_client = boto3.client('iam', region_name=region)

qs_config_id = os.environ['QSConfigId']
delegated_admin_account = os.environ['DelegatedAdminAccount']
account_id = os.environ['AccountId']
qs_version = os.environ['QuickSetupVersion']

tags = [{'Key': 'QuickSetupID', 'Value': qs_config_id},
        {'Key': 'QuickSetupType', 'Value': 'OpsCenter'},
        {'Key': 'QuickSetupVersion', 'Value': qs_version}]


''' 
This method creates role, attaches a managed policy and inline policy, if any.
If role already exists and is created by QuickSetup, we will update the policy document.
'''
def setup_customer_role_with_policies(role_name, assume_role_policy_doc, role_desc, managed_policy_arn, role_tags=()):
    try:
        iam_client.create_role(AssumeRolePolicyDocument = assume_role_policy_doc, Description=role_desc, RoleName=role_name, Tags=role_tags)
        logger.info(f'Successfully created the role {role_name} {account_id}')
        if managed_policy_arn:
            iam_client.attach_role_policy(RoleName=role_name, PolicyArn=managed_policy_arn)
        logger.info(f'Successfully created the role policy {role_name} {account_id}')
    except ClientError as e:
        if e.response['Error']['Code'] == 'EntityAlreadyExists':
            logger.info(f'Role={role_name} already exists. Reusing the role and trying to update the role policy')
            if is_role_created_by_qs(role_name):
                try:
                    if managed_policy_arn:
                        iam_client.attach_role_policy(RoleName=role_name, PolicyArn=managed_policy_arn)
                        logger.info(f'Successfully updated the role managed-policy for {role_name}')
                    else:
                        logger.info(f'No action taken as managed_policy_name to update is empty for {role_name}')

                    if assume_role_policy_doc:
                        iam_client.update_assume_role_policy(RoleName=role_name, PolicyDocument=assume_role_policy_doc)
                        logger.info(f'Successfully updated the role assume-role-policy for {role_name}')
                    else:
                        logger.info('No action taken as assume_role_policy_doc to update is empty for {role_name}')

                except ClientError as e:
                    logger.exception(f'Encountered error:{ str(e)}')
                    raise e
        elif e.response['Error']['Code'] == 'UnmodifiableEntityException':
            logger.info(f'Policy: {managed_policy_arn} already attached to role: {role_name} {account_id}, no action taken')
        else:
            logger.exception(f'Role={role_name} creation failed. Error is {str(e)}')
            raise e


'''
This method creates AWSServiceRoleForAmazonSSM_AccountDiscovery SLR.
'''
def create_account_discovery_service_role():
    try:
        role_name = 'AWSServiceRoleForAmazonSSM_AccountDiscovery';
        service_principal = 'accountdiscovery.ssm.amazonaws.com';
        iam_client.create_service_linked_role(AWSServiceName=service_principal);
        logger.info(f'Successfully created the role {role_name}')
    except ClientError as e:
        # Returns an InvalidInput error rather than EntityAlreadyExists
        if e.response['Error']['Code'] == "InvalidInput":
            logger.info(f'{role_name} already exists.')
            return
        logger.exception(f'Role={role_name} creation failed. Error is {str(e)}')
        raise e

'''
Checks if given role_name is created by QS by validating the tags
'''
def is_role_created_by_qs(role_name):
    role_tags = iam_client.list_role_tags(RoleName=role_name)['Tags']
    return any(tag['Key'] == 'QuickSetupID' for tag in role_tags)

'''
Checks if given policy is created by QS by validating the tags
'''
def is_policy_created_by_qs(policy_arn):
    policy_tags = iam_client.list_policy_tags(PolicyArn=policy_arn)['Tags']
    return any(tag['Key'] == 'QuickSetupID' for tag in policy_tags)


'''
Creates customer managed policy. If policy already exists, we reuse the existing policy.
'''
def create_customer_managed_policy(policy_name, policy_document, description, policy_tags):
    try:
        customer_managed_policy = iam_client.create_policy(PolicyName=policy_name, PolicyDocument=policy_document, Description=description,Tags=policy_tags)
        return customer_managed_policy.get('Policy').get('Arn')
    except ClientError as e:
        if e.response['Error']['Code'] == 'EntityAlreadyExists':
            logger.info(f'Policy={policy_name} already exists. Reusing the role')
            return 'arn:%s:iam::%s:policy/%s'%(partition, account_id, policy_name)
        else:
            logger.exception(f'Policy={policy_name} creation failed. Error is {str(e)}')
            raise e

'''
This method creates AWS-SystemsManager-AutomationAdministrationRole role
'''
def setup_automation_administration_role():
    role_name = 'AWS-SystemsManager-AutomationAdministrationRole'
    desc = 'Configure the AWS-SystemsManager-AutomationAdministrationRole to enable use of AWS Systems Manager Cross Account/Region Automation execution.'
    policy_doc = json.dumps({
        'Version': '2012-10-17',
        'Statement': [
            {
                'Effect': 'Allow',
                'Principal': {
                    'Service': 'ssm.amazonaws.com'
                },
                'Action': 'sts:AssumeRole'
            }
        ]
    })
    managed_policy_name = 'AssumeRole-AWSSystemsManagerAutomationExecutionRole'
    managed_policy_doc = json.dumps({
        'Version': '2012-10-17',
        'Statement': [
            {
                'Effect': 'Allow',
                'Resource': f'arn:{partition}:iam::*:role/AWS-SystemsManager-AutomationExecutionRole',
                'Action': ['sts:AssumeRole']
            },
            {
                'Effect': 'Allow',
                'Resource': '*',
                'Action': ['organizations:ListAccountsForParent']
            }
        ]
    })

    managed_policy_arn = create_customer_managed_policy(managed_policy_name, managed_policy_doc, '', tags)
    setup_customer_role_with_policies(role_name, policy_doc, desc,managed_policy_arn, tags)

'''
This method creates OpsItem-CrossAccountManagementRole role
'''
def setup_opsitem_crossaccount_management_role():
    role_name = 'OpsItem-CrossAccountManagementRole'
    desc = 'AWS Systems Manager OpsCenter management account for cross-account OpsItem management.'
    policy_doc = json.dumps({
        'Version': '2012-10-17',
        'Statement': [
            {
                'Effect': 'Allow',
                'Principal': {
                    'Service': 'ssm.amazonaws.com'
                },
                'Action': 'sts:AssumeRole',
                'Condition': {
                    'StringEquals': {
                        'aws:SourceAccount': delegated_admin_account
                    },
                    "ArnEquals": {
                        "aws:SourceArn": f"arn:{partition}:ssm:*:{delegated_admin_account}:opsitem/*"
                    }
                }
            }
        ]
    })
    managed_policy_name = 'OpsItemCrossAccountManagementRolePolicy'
    managed_policy_desc = 'Managed policy for the IAM OpsItemCrossAccountManagementRole.'
    managed_policy_doc = json.dumps({
        'Version': '2012-10-17',
        'Statement': [
            {
                'Effect': 'Allow',
                'Resource': f'arn:{partition}:iam::*:role/OpsItem-*Role*',
                'Action': ['sts:AssumeRole']
            },
            {
                'Effect': 'Allow',
                'Resource': '*',
                'Action': ['organizations:DescribeAccount']
            }
        ]
    })

    managed_policy_arn = create_customer_managed_policy(managed_policy_name, managed_policy_doc, managed_policy_desc, tags)
    setup_customer_role_with_policies(role_name,policy_doc,desc,managed_policy_arn, tags)

'''
This method deletes OpsItem-CrossAccountManagementRole role, if created by QS
'''
def delete_opsitem_crossaccount_management_role():
    role_name = 'OpsItem-CrossAccountManagementRole'
    policy_name= 'OpsItemCrossAccountManagementRolePolicy'
    policy_arn = f'arn:{partition}:iam::{account_id}:policy/{policy_name}'
    try:
        if is_role_created_by_qs(role_name):
            logger.info(f'{role_name} Role is created by QS')
            iam_client.detach_role_policy(RoleName=role_name, PolicyArn=policy_arn);
            if is_policy_created_by_qs(policy_arn):
                iam_client.delete_policy(PolicyArn=policy_arn)
            iam_client.delete_role(RoleName=role_name)
            logger.info(f'{role_name} is deleted')
        else:
            logger.info(f'{role_name} is not created by QS')
    except ClientError as e:
        if e.response['Error']['Code'] == 'NoSuchEntity':
            logger.info(f'Role={role_name} is already deleted.')
        elif e.response['Error']['Code'] == 'UnmodifiableEntity':
            logger.info(f'Role: {role_name}  cannot be deleted or policy cannot be detached becuase of {str(e)}')
        else:
            logger.exception(f'Role={role_name} deletion failed. Error is {str(e)}')
            raise e


def lambda_handler(event, context):
    request_type = event.get('RequestType')
    try:
        # AutomationAdministrationRole will not be deleted or updated.
        # This is because the role could be shared by other ssm services.
        if request_type == CREATE:
            # AccountDiscoverySLR is created in ManagementAccount as part of QS common code.
            # Below code is added to create SLR in Delegated Admin
            logger.info('Create Event is triggered')
            #Check if current account is Delegated Admin
            create_account_discovery_service_role()
            setup_opsitem_crossaccount_management_role()
            setup_automation_administration_role()
        elif request_type == DELETE:
            logger.info('Delete Event is triggered')
            delete_opsitem_crossaccount_management_role()
        elif request_type == UPDATE:
            logger.info('Update Event is triggered')
            #Currently we expect update scenarios during any bug fixes
            setup_opsitem_crossaccount_management_role()
            setup_automation_administration_role()
        else:
            logger.info(f'Unknown operation: {request_type}')
        #CloudFormation compares the physical resource id returned by your Lambda function to the one you returned previously.
        #If the IDs are different, CloudFormation assumes the resource has been replaced with a new resource.
        #When the resource update logic completes successfully, a Delete request is sent with the old physical resource id.
        #If the stack update fails and a rollback occurs, the new physical resource id is sent in the Delete event.
        cfnresponse.send(event, context, cfnresponse.SUCCESS, None, 'OpsCenterAdminCustomResources')
        return cfnresponse.SUCCESS
    except Exception as e:
        error_reason = f'{str(e)}. See the details in CloudWatch Log Stream: {context.log_stream_name} in Log Group {context.log_group_name}'
        cfnresponse.send(event, context, cfnresponse.FAILED, None, 'OpsCenterAdminCustomResources', reason = error_reason)
        raise e
