import logging
import time
from typing import List
import json
import uuid
import sys
import subprocess

import boto3
from botocore.exceptions import ClientError


logger = logging.getLogger(__name__)
logger.setLevel(logging.INFO)



def get_efs_policy(events: dict, context: dict) -> dict:
    efs_client = boto3.client("efs")

    file_system_id: str = events.get("FileSystemId") or ""
    efs_arn = efs_client.describe_file_systems(FileSystemId=file_system_id)[
        "FileSystems"
    ][0]["FileSystemArn"]
    try:
        efs_policy = efs_client.describe_file_system_policy(
            FileSystemId=file_system_id
        )["Policy"]
    except ClientError as error:
        if error.response["Error"]["Code"] == "PolicyNotFound":
            return {"EfsArn": efs_arn, "EfsPolicy": None}
        else:
            raise error

    return {"EfsArn": efs_arn, "EfsPolicy": efs_policy}
