function VerifySignature { param( [Parameter(Mandatory=$true)] $FilePath, [Parameter(Mandatory=$true)] $SignatureFilePath ) $code = " using System; using System.IO; using System.Security.Cryptography; namespace Crypto { public class CryptoHelper { public static bool VerifySignature(string FilePath, string SignatureFilePath) { try { var modulus = Convert.FromBase64String(""""); var exponent = Convert.FromBase64String(""AQAB""); var rsa = RSA.Create(new RSAParameters {Exponent = exponent, Modulus = modulus}); using (var stream = File.OpenRead(FilePath)) { var signatureBytes = File.ReadAllBytes(SignatureFilePath); var bytesToVerify = SHA256.Create().ComputeHash(stream); return rsa.VerifyData(bytesToVerify, signatureBytes, HashAlgorithmName.SHA256, RSASignaturePadding.Pkcs1); } } catch (Exception e) { Console.WriteLine(e.ToString()); return false; } } } } "; Add-Type -TypeDefinition $code -Language CSharp -ErrorAction Stop; return [Crypto.CryptoHelper]::VerifySignature($FilePath, $SignatureFilePath); }
function ExecuteScript { param( [Parameter(Mandatory=$true)] $ScriptFileName, [Parameter(Mandatory=$true)] $ScriptExpression )
$ExitCode = 1; $Result = ""; $TempFolder = Join-Path -Path ([System.IO.Path]::GetTempPath()) -ChildPath ([System.Guid]::NewGuid()); $null = New-Item -ItemType Directory -Path $TempFolder -ErrorAction Stop; try { $MaintenanceZip = "aws-maintenance.zip"; $MaintenanceZipSig = "aws-maintenance.zip.sig"; $Prefix = "3a98e56d-8425-4325-87f2-b1805ab883c3"; $Stage  = "prod"; $Region = "{{global:REGION}}"; $S3Domain = "s3.amazonaws.com"; if ($Region.Contains("gov")) { $S3Domain = "s3-$Region.amazonaws.com"; } $URI="https://launchwizardscript-$Stage-$Region.$S3Domain/$Prefix/Maintenance/"; $ZipFile = Join-Path -Path $TempFolder -ChildPath $MaintenanceZip; $ZipFileSig = Join-Path -Path $TempFolder -ChildPath $MaintenanceZipSig; $ScriptPath = Join-Path -Path $TempFolder -ChildPath "Maintenance"; $ScriptPath = Join-Path -Path $ScriptPath -ChildPath $ScriptFileName; $ProgressPreference = "SilentlyContinue"; $global:ProgressPreference = "SilentlyContinue"; $null = Invoke-WebRequest -Uri ($URI+$MaintenanceZip) -OutFile $ZipFile -ErrorAction Stop; $null = Invoke-WebRequest -Uri ($URI+$MaintenanceZipSig) -OutFile $ZipFileSig -ErrorAction Stop; if ((VerifySignature $ZipFile $ZipFileSig) -ne $true) { throw "Signature validation failed" }; $null = Expand-Archive -Path $ZipFile -DestinationPath $TempFolder -ErrorAction Stop; $ScriptBlock = [scriptblock]::Create(". $ScriptPath; $ScriptExpression"); $Result = Invoke-Command -ScriptBlock $ScriptBlock -ErrorAction Stop; } catch { Write-Output $_.Exception; } finally { $Null = Remove-Item $TempFolder -Recurse -Force -ErrorAction SilentlyContinue; if (($null -eq $Result) -or ($Result.GetType().FullName -eq "System.String")) { Write-Output "Unknown execution failure $Result"; exit(1) } else { $Len = $Result.Length-1; $ExitCode = $Result[$Len]; $Len2 = $Len-1; Write-Output $Result[0..$Len2]; exit($ExitCode) } } } try { Set-StrictMode -Version latest; $ScriptExpression = "CheckDatabase -Databases {{Database}}  -SecretsManagerId {{SecretsMangerCredential}} " + "-CheckOptions TablesCheck={{TablesCheck}},FileGroupsCheck={{FileGroupsCheck}}," + "AllocationCheck={{AllocationCheck}},CatalogCheck={{CatalogCheck}}," + "PhysicalOnlyCheck={{PhysicalOnlyCheck}},DataPurityCheck={{DataPurityCheck}}," + "NonClustedIndexCheck={{NonClustedIndexCheck}},PerformExtendedCheck={{PerformExtendedCheck}}," + "UseLocks={{UseLocks}}";
ExecuteScript -ScriptFileName "aws-sqldbcc.ps1" -ScriptExpression $ScriptExpression; } catch { Write-Output $_.Exception; exit(1) }
