"""
Script to list parameter groups and option groups associated with RDS instances, RDS clusters, and RDS snapshots.
"""

import sys

import boto3

sys.tracebacklimit = 0
rds = boto3.client("rds")


def script_handler(event: dict, _):
    """
    Main function that retrieves and processes RDS resources.

    Args:
        event (dict): Dictionary with the following parameters:
            DeleteInstanceParameterGroups (bool): Indicates whether to list instance parameter groups.
            DeleteClusterParameterGroups (bool): Indicates whether to list cluster parameter groups.
            DeleteOptionGroups (bool): Indicates whether to list option groups.
        _: unused context object

    Returns:
        dict: Dictionary with the following keys:
            attached_instance_parameter_groups (str): Comma-separated string of associated instance parameter groups.
            attached_cluster_parameter_groups (str): Comma-separated string of associated cluster parameter groups.
            attached_option_groups (str): Comma-separated string of associated option groups.
    """

    delete_instance_parameter_groups = event.get("DeleteInstanceParameterGroups", False)
    delete_cluster_parameter_groups = event.get("DeleteClusterParameterGroups", False)
    delete_option_groups = event.get("DeleteOptionGroups", False)

    attached_instance_parameter_groups = []
    attached_cluster_parameter_groups = []
    attached_option_groups = []

    if delete_instance_parameter_groups or (not delete_cluster_parameter_groups and not delete_option_groups):
        paginator = rds.get_paginator("describe_db_instances")
        page_iterator = paginator.paginate()
        for page in page_iterator:
            instances = page.get("DBInstances", [])
            for instance in instances:
                if instance.get("DBParameterGroups"):
                    parameter_group_name = instance["DBParameterGroups"][0]["DBParameterGroupName"]
                    if not parameter_group_name.startswith("default."):
                        attached_instance_parameter_groups.append(parameter_group_name)

    if delete_cluster_parameter_groups or (not delete_instance_parameter_groups and not delete_option_groups):
        paginator = rds.get_paginator("describe_db_clusters")
        page_iterator = paginator.paginate()
        for page in page_iterator:
            clusters = page.get("DBClusters", [])
            for cluster in clusters:
                if cluster.get("DBClusterParameterGroup"):
                    parameter_group_name = cluster["DBClusterParameterGroup"]
                    if not parameter_group_name.startswith("default."):
                        attached_cluster_parameter_groups.append(parameter_group_name)

    if delete_option_groups or (not delete_instance_parameter_groups and not delete_cluster_parameter_groups):
        paginator = rds.get_paginator("describe_db_instances")
        page_iterator = paginator.paginate()
        for page in page_iterator:
            instances = page.get("DBInstances", [])
            for instance in instances:
                if instance.get("OptionGroupMemberships"):
                    for option_group in instance["OptionGroupMemberships"]:
                        option_group_name = option_group["OptionGroupName"]
                        if not option_group_name.startswith("default."):
                            attached_option_groups.append(option_group_name)

        paginator = rds.get_paginator("describe_db_clusters")
        page_iterator = paginator.paginate()
        for page in page_iterator:
            clusters = page.get("DBClusters", [])
            for cluster in clusters:
                if cluster.get("AssociatedRoles"):
                    for role in cluster["AssociatedRoles"]:
                        if role.get("FeatureName") == "OPT_IN_REQUIRED" and role.get("OptionGroupName"):
                            option_group_name = role["OptionGroupName"]
                            if not option_group_name.startswith("default."):
                                attached_option_groups.append(option_group_name)

        paginator = rds.get_paginator("describe_db_snapshots")
        page_iterator = paginator.paginate()
        for page in page_iterator:
            snapshots = page.get("DBSnapshots", [])
            for snapshot in snapshots:
                if snapshot.get("OptionGroupMemberships"):
                    for option_group in snapshot["OptionGroupMemberships"]:
                        option_group_name = option_group["OptionGroupName"]
                        if not option_group_name.startswith("default."):
                            attached_option_groups.append(option_group_name)

    if not attached_instance_parameter_groups and (
        delete_instance_parameter_groups or (not delete_cluster_parameter_groups and not delete_option_groups)
    ):
        attached_instance_parameter_groups.append("No attached instance parameter groups")
    if not attached_cluster_parameter_groups and (
        delete_cluster_parameter_groups or (not delete_instance_parameter_groups and not delete_option_groups)
    ):
        attached_cluster_parameter_groups.append("No attached cluster parameter groups")
    if not attached_option_groups and (
        delete_option_groups or (not delete_instance_parameter_groups and not delete_cluster_parameter_groups)
    ):
        attached_option_groups.append("No attached option groups")

    comma_separated_instance_parameter_groups = ", ".join(attached_instance_parameter_groups)
    comma_separated_cluster_parameter_groups = ", ".join(attached_cluster_parameter_groups)
    comma_separated_option_groups = ", ".join(attached_option_groups)

    return {
        "attached_instance_parameter_groups": comma_separated_instance_parameter_groups,
        "attached_cluster_parameter_groups": comma_separated_cluster_parameter_groups,
        "attached_option_groups": comma_separated_option_groups,
    }
