def get_source_details(events, context):
    src_ip = events['SourceIP']
    src_vpc = events['SourceVpc']
    dst_ip = events['DestinationIP']

    if src_ip == dst_ip:
        raise Exception('Input parameters SourceIP and DestinationIP are same, kindly provide different input parameters')

    import boto3

    ec2_client = boto3.client('ec2')

    filters = [
        {
            'Name': 'addresses.private-ip-address',
            'Values': [
                src_ip
            ]
        }
    ]
    if src_vpc != '':
        filters.append(
                {
                    'Name': 'vpc-id',
                    'Values': [
                        src_vpc
                    ]
                }
        )

    network_interface = ec2_client.describe_network_interfaces(Filters=filters)

    if len(network_interface['NetworkInterfaces']) > 0:
        network_interface = network_interface['NetworkInterfaces'][0]
        if 'AttachTime' in str(network_interface):
            network_interface['Attachment']['AttachTime'] = str(network_interface['Attachment']['AttachTime'])
    else:
        raise Exception('Kindly check the SourceIP and SourceVpc input parameters, no network interface found')

    output = {
        'SrcNetworkInterface': network_interface
    }
    output['SrcVpcId'] = network_interface['VpcId']
    output['SrcSubnetId'] = network_interface['SubnetId']
    output['SrcSecurityGroups'] = [group['GroupId'] for group in network_interface['Groups']]            

    routes = ec2_client.describe_route_tables(
        Filters=[
        {
            'Name': 'association.subnet-id',
            'Values': [
                network_interface['SubnetId']
            ]
        }
    ]
    )

    if len(routes['RouteTables']) > 0:
        routes = routes['RouteTables'][0]['Routes']
    # if RouteTable is not returned, this indicates association of subnet with main route table
    else:
        try:
            routes = ec2_client.describe_route_tables(
                Filters=[
                    {
                        'Name': 'association.main',
                        'Values': [
                            'true'
                        ]
                    },
                    {
                        'Name': 'vpc-id',
                        'Values': [
                            network_interface['VpcId']
                        ]
                    }                    
                ]
            )['RouteTables'][0]['Routes']
        except Exception as e:
            raise Exception('Could not find route table for input SourceIP')

    output['Routes'] = routes
    return output

def get_next_hop(events, context):
    from ipaddress import ip_network,ip_address
    routes = events['Routes'] 
    destination = ip_address(events['DestinationIP'])        
    most_specific = -1

    AWS_DOC = '\nWorking with route tables - https://docs.aws.amazon.com/vpc/latest/userguide/WorkWithRouteTables.html'
    for route in routes:
        if route.get('DestinationCidrBlock'):
            mask = int(route['DestinationCidrBlock'].split('/')[1])
            if not 'DestinationPrefixListId' in str(route) and destination in ip_network(route['DestinationCidrBlock'], strict=False) and mask > most_specific:
                if route['State'] != 'blackhole':
                    most_specific = mask
                    next_hop = route
    #0.0.0.0/0
    if most_specific >= 0:
        return next_hop.get('EgressOnlyInternetGatewayId') or next_hop.get('GatewayId') or next_hop.get('InstanceId') or next_hop.get('NetworkInterfaceId') or next_hop.get('LocalGatewayId') or next_hop.get('NatGatewayId') or next_hop.get('TransitGatewayId') or next_hop.get('VpcPeeringConnectionId')
    raise Exception('No route found for destination: ' + events['DestinationIP'] + AWS_DOC)

def check_destination_vpcid(events, context):
    dst_vpc_from_input = events['InputDstVpc']
    dst_vpc_from_route = events['RouteDstVpc']

    AWS_DOC = '\nWorking with route tables - https://docs.aws.amazon.com/vpc/latest/userguide/WorkWithRouteTables.html'

    # ensure that dst_vpc is not blank, as it's an optinal parameter
    if dst_vpc_from_input != '':
        if dst_vpc_from_input == dst_vpc_from_route:
            return True
        else:
            raise Exception('Kindly check the source route table to ensure a more specific route is present towards required destination VPC' + AWS_DOC)

def eval_sg_rules(events, context):
    from ipaddress import ip_network,ip_address
    src_security_groups = events['SrcSecurityGroups']
    dst_security_groups = events['DstSecurityGroups']
    security_group_details = events['SecurityGroups']
    src_ip = ip_address(events['SourceIP'])
    dst_ip = ip_address(events['DestinationIP'])        
    dst_port = int(events['DestinationPort'])    

    AWS_DOC = '\nSecurity groups for your VPC - Working with security groups - https://docs.aws.amazon.com/vpc/latest/userguide/VPC_SecurityGroups.html#WorkingWithSecurityGroups'      

    def eval_src_egress_rules():                    
        for src_security_group in src_security_groups:
            sg = [group for group in security_group_details['SecurityGroups'] if group['GroupId'] == src_security_group][0]
            for rule in sg['IpPermissionsEgress']:
                if ((rule.get('IpProtocol') == '-1') or (rule.get('FromPort') == -1 and rule.get('ToPort') == -1) or (dst_port in range(rule.get('FromPort'), rule.get('ToPort') +1))):
                    for cidr in rule['IpRanges']:
                        if dst_ip in ip_network(cidr['CidrIp'], strict=False):
                            return True
                    else:
                        for group in rule['UserIdGroupPairs']:
                            if any(sg in group['GroupId'] for sg in dst_security_groups) :
                                return True
        raise Exception('Egress rules on source do not allow traffic towards destination: ' + events['DestinationIP'] + ':' + str(dst_port) + AWS_DOC)               

    def eval_dst_ingress_rules():
        for dst_security_group in dst_security_groups:
            sg = [group for group in security_group_details['SecurityGroups'] if group['GroupId'] == dst_security_group][0]
            for rule in sg['IpPermissions']:
                if ((rule.get('IpProtocol') == '-1') or (rule.get('FromPort') == -1 and rule.get('ToPort') == -1) or (dst_port in range(rule.get('FromPort'), rule.get('ToPort') +1))):
                    for cidr in rule['IpRanges']:
                        if src_ip in ip_network(cidr['CidrIp'], strict=False):
                            return True
                    else:
                        for group in rule['UserIdGroupPairs']:
                            if any(sg in group['GroupId'] for sg in src_security_groups):
                                return True
        raise Exception('Ingress rules on destination do not allow traffic from source: ' + events['SourceIP'] + ' towards destination port ' + str(dst_port) + AWS_DOC)

    eval_src_egress_rules()        
    eval_dst_ingress_rules()         

    return True

def eval_nacls(events, context):
    from ipaddress import ip_network,ip_address
    src_subnet_id = events['SrcSubnetId']
    dst_subnet_id = events['DstSubnetId']
    src_port_range = events['SourcePortRange']
    src_port_from = int(src_port_range.split('-')[0])
    src_port_to = int(src_port_range.split('-')[1])

    AWS_DOC = '\nNetwork ACLs - Working with network ACLs - https://docs.aws.amazon.com/vpc/latest/userguide/vpc-network-acls.html#nacl-tasks'

    if src_subnet_id == dst_subnet_id:
        return True

    src_ip = ip_address(events['SourceIP'])        
    dst_ip = ip_address(events['DestinationIP'])        
    dst_port = int(events['DestinationPort'])

    src_acls = [acl['Entries'] for acl in events['NetworkAcls'] if src_subnet_id in str(acl['Associations'])][0]
    dst_acls = [acl['Entries'] for acl in events['NetworkAcls'] if dst_subnet_id in str(acl['Associations'])][0]

    def eval_src_nacls(acls):                    

        def check_egress_acls(acls, dst_ip, dst_port):
            for acl in acls:
                # check ipv4 acl rule only
                if acl.get('CidrBlock'):
                    #Check IP
                    if dst_ip in ip_network(acl['CidrBlock'], strict=False):
                        #Check Port
                        if ((acl.get('Protocol') == '-1') or (dst_port in range(acl['PortRange']['From'], acl['PortRange']['To'] +1))):
                            #Check Action
                            if acl['RuleAction'] == 'allow':
                                return True
                            else: raise Exception('Source Subnet Network Acl Egress Rules do not allow outbound traffic to destination: ' + events['DestinationIP'] + ':' + str(events['DestinationPort']) + AWS_DOC)
            else:
                raise Exception('Source Subnet Network Acl Egress Rules do not allow outbound traffic to destination: ' + events['DestinationIP'] + ':' + str(events['DestinationPort']) + AWS_DOC)

        def check_ingress_acls(acls, src_ip):
            for acl in acls:
                # check ipv4 acl rule only
                if acl.get('CidrBlock'):
                    #Check IP
                    if src_ip in ip_network(acl['CidrBlock'], strict=False):
                        #Check Port
                        if ((acl.get('Protocol') == '-1') or (set(range(src_port_from, src_port_to)).issubset(range(acl['PortRange']['From'], acl['PortRange']['To'] +1)))):
                            #Check Action
                            if acl['RuleAction'] == 'allow':
                                return True
                            else: raise Exception('Source Subnet Network Acl Ingress Rules do not allow inbound traffic from destination: ' + events['DestinationIP'] + AWS_DOC)            
            else:
                raise Exception('Source Subnet Network Acl Ingress Rules do not allow inbound traffic from destination: ' + events['DestinationIP'] + AWS_DOC)            

        egress_acls = [acl for acl in acls if acl['Egress']]                                
        ingress_acls = [acl for acl in acls if not acl['Egress']]

        src_egress_check_pass = check_egress_acls(egress_acls, dst_ip, dst_port)
        src_ingress_check_pass= check_ingress_acls(ingress_acls, dst_ip) 

        if src_ingress_check_pass and src_egress_check_pass: 
            return True

    def eval_dst_nacls(acls):                                    
        def check_egress_acls(acls, dst_ip):
            for acl in acls:
                # check ipv4 acl rule only
                if acl.get('CidrBlock'):
                    #Check IP
                    if dst_ip in ip_network(acl['CidrBlock'], strict=False):
                        #Check Port
                        if ((acl.get('Protocol') == '-1') or (set(range(src_port_from,src_port_to)).issubset(range(acl['PortRange']['From'], acl['PortRange']['To'] +1)))):
                            #Check Action
                            if acl['RuleAction'] == 'allow':
                                return True
                            else: raise Exception('Destination Subnet Network Acl Egress Rules do not allow outbound traffic to source: ' + events['SourceIP'] + AWS_DOC)
            else:
                raise Exception('Destination Subnet Network Acl Egress Rules do not allow outbound traffic to source: ' + events['SourceIP'] + AWS_DOC)

        def check_ingress_acls(acls, src_ip, dst_port):            
            for acl in acls:
                # check ipv4 acl rule only
                if acl.get('CidrBlock'):
                    #Check IP
                    if src_ip in ip_network(acl['CidrBlock'], strict=False):
                        #Check Port
                        if ((acl.get('Protocol') == '-1') or (dst_port in range(acl['PortRange']['From'], acl['PortRange']['To'] +1))):
                            #Check Action
                            if acl['RuleAction'] == 'allow':
                                return True
                            else: raise Exception('Destination Subnet Network Acl Ingress Rules do not allow inbound traffic from source: ' + events['SourceIP'] + ' towards destination port ' + str(events['DestinationPort']) + AWS_DOC)
            else:
                raise Exception('Destination Subnet Network Acl Ingress Rules do not allow inbound traffic from source: ' + events['SourceIP'] + ' towards destination port ' + str(events['DestinationPort']) + AWS_DOC)            

        egress_acls = [acl for acl in acls if acl['Egress']]                                
        ingress_acls = [acl for acl in acls if not acl['Egress']]

        dst_ingress_check_pass= check_ingress_acls(ingress_acls, src_ip, dst_port) 
        dst_egress_check_pass = check_egress_acls(egress_acls, src_ip)

        if dst_ingress_check_pass and dst_egress_check_pass: 
            return True

    eval_src_nacls(src_acls)
    eval_dst_nacls(dst_acls)

    return True

def eval_src_public_ip(events, context):
    src_network_interface = events['SrcNetworkInterface']
    private_ips = src_network_interface['PrivateIpAddresses']
    src_ip = events['SourceIP']
    AWS_DOC = '\nAmazon EC2 instance IP addressing - Working with IP addresses for your instance - https://docs.aws.amazon.com/AWSEC2/latest/UserGuide/using-instance-addressing.html#working-with-ip-addresses'

    for private_ip in private_ips:
        if private_ip['PrivateIpAddress'] == src_ip:
            if 'PublicIp' in str(private_ip):
                return True

    raise Exception('A public IP or Elastic IP is required at source to connect to a public destination' + AWS_DOC)

