def eval_nat_nacls(events, context):
    from ipaddress import ip_network,ip_address
    src_subnet_id = events['SrcSubnetId']
    nat_subnet_id = events['NatSubnetId']

    src_ip = ip_address(events['SourceIP'])        
    dst_ip = ip_address(events['DestinationIP'])        
    dst_port = int(events['DestinationPort'])
    src_port_range = events['SourcePortRange']
    src_port_from = int(src_port_range.split('-')[0])
    src_port_to = int(src_port_range.split('-')[1])

    nat_acls = events['NatNetworkAcls'][0]['Entries']
    egress_acls = [acl for acl in nat_acls if acl['Egress']]                                
    ingress_acls = [acl for acl in nat_acls if not acl['Egress']]

    AWS_DOC = '\nNetwork ACLs - Working with network ACLs - https://docs.aws.amazon.com/vpc/latest/userguide/vpc-network-acls.html#nacl-tasks'

    def check_egress_towards_dst(acls, dst_ip, dst_port):            
        for acl in acls:
            # check ipv4 acl rule only
            if acl.get('CidrBlock'):
                #Check IP
                if dst_ip in ip_network(acl['CidrBlock'], strict=False):
                    #Check Port
                    if ((acl.get('Protocol') == '-1') or (dst_port in range(acl['PortRange']['From'], acl['PortRange']['To'] +1))):
                        #Check Action
                        if acl['RuleAction'] == 'allow':
                            return True
                        else: raise Exception('NatGateway Subnet ' + events['SrcSubnetId'] + ' Network Acl Egress Rules do not allow outbound traffic to destination: ' + events['DestinationIP'] + ':' + str(events['DestinationPort']) + AWS_DOC)
        else:
            raise Exception('NatGateway Subnet ' + events['SrcSubnetId'] + ' Network Acl Egress Rules do not allow outbound traffic to destination: ' + events['DestinationIP'] + ':' + str(events['DestinationPort']) + AWS_DOC)

    def check_ingress_from_dst(acls, src_ip):
        for acl in acls:
            # check ipv4 acl rule only
            if acl.get('CidrBlock'):
                #Check IP
                if src_ip in ip_network(acl['CidrBlock'], strict=False):
                    #Check Port
                    if ((acl.get('Protocol') == '-1') or (set(range(src_port_from,src_port_to)).issubset(range(acl['PortRange']['From'], acl['PortRange']['To'] +1)))):
                        #Check Action
                        if acl['RuleAction'] == 'allow':
                            return True
                        else: raise Exception('NatGateway Subnet ' + events['SrcSubnetId'] + ' Network Acl Ingress Rules do not allow inbound traffic from destination: ' + events['DestinationIP'] + AWS_DOC)
        else:
            raise Exception('NatGateway Subnet ' + events['SrcSubnetId'] + ' Network Acl Ingress Rules do not allow inbound traffic from destination: ' + events['DestinationIP'] + AWS_DOC)

    def check_ingress_from_src(acls, src_ip, dst_port):
        for acl in acls:
            # check ipv4 acl rule only
            if acl.get('CidrBlock'):
                #Check IP
                if src_ip in ip_network(acl['CidrBlock'], strict=False):
                    #Check Port
                    if ((acl.get('Protocol') == '-1') or (dst_port in range(acl['PortRange']['From'], acl['PortRange']['To'] +1))):
                        #Check Action
                        if acl['RuleAction'] == 'allow':
                            return True
                        else: raise Exception('NatGateway Subnet Network Acl Ingress Rules do not allow inbound traffic from source: ' + events['SourceIP'] + ' towards destination port ' + str(events['DestinationPort']) + AWS_DOC)
        else:
            raise Exception('NatGateway Subnet Network Acl Ingress Rules do not allow inbound traffic from source: ' + events['SourceIP'] + ' towards destination port ' + str(events['DestinationPort']) + AWS_DOC)

    def check_egress_towards_src(acls, dst_ip):
        for acl in acls:
            # check ipv4 acl rule only
            if acl.get('CidrBlock'):
                #Check IP
                if dst_ip in ip_network(acl['CidrBlock'], strict=False):
                    #Check Port
                    if ((acl.get('Protocol') == '-1') or (set(range(src_port_from,src_port_to)).issubset(range(acl['PortRange']['From'], acl['PortRange']['To'] +1)))):
                        #Check Action
                        if acl['RuleAction'] == 'allow':
                            return True
                        else: raise Exception('NatGateway Subnet Network Acl Egress Rules do not allow outbound traffic to source: ' + events['SourceIP'] + AWS_DOC)
        else:
            raise Exception('NatGateway Subnet Network Acl Egress Rules do not allow outbound traffic to source: ' + events['SourceIP'] + AWS_DOC)

    check_egress_towards_dst(egress_acls, dst_ip, dst_port)
    check_ingress_from_dst(ingress_acls, dst_ip)

    if src_subnet_id == nat_subnet_id:
        return True

    check_ingress_from_src(ingress_acls, src_ip, dst_port)
    check_egress_towards_src(egress_acls, src_ip)

    return True

def get_nat_route_details(events, context):
    output = {}
    import boto3
    ec2_client = boto3.client('ec2')
    routes = ec2_client.describe_route_tables(
        Filters=[
        {
            'Name': 'association.subnet-id',
            'Values': [
                events['NatSubnetId']
            ]
        }
    ]
    )

    if len(routes['RouteTables']) > 0:
        routes = routes['RouteTables'][0]['Routes']
    # if RouteTable is not returned, this indicates association of subnet with main route table
    else:
        try:
            routes = ec2_client.describe_route_tables(
                Filters=[
                    {
                        'Name': 'association.main',
                        'Values': [
                            'true'
                        ]
                    },
                    {
                        'Name': 'vpc-id',
                        'Values': [
                            events['NatVpcId']
                        ]
                    }                    
                ]
            )['RouteTables'][0]['Routes']
        except Exception as e:
            raise Exception('Could not find route table for NAT Gateway')

    output['NatRoutes'] = routes
    return output

def get_nat_next_hop(events, context):
    from ipaddress import ip_network,ip_address
    src_subnet_id = events['SrcSubnetId']
    nat_subnet_id = events['NatSubnetId']

    AWS_DOC = '\nNAT gateways - Working with NAT gateways - https://docs.aws.amazon.com/vpc/latest/userguide/vpc-nat-gateway.html#nat-gateway-working-with'

    if src_subnet_id == nat_subnet_id:
        raise Exception('NatGateway and Source cannot be placed in the same subnet, NatGateway should be in a public subnet' + AWS_DOC)

    routes = events['NatRoutes'] 
    destination = ip_address(events['DestinationIP'])        
    most_specific = -1       

    for route in routes:
        if route.get('DestinationCidrBlock'):
            mask = int(route['DestinationCidrBlock'].split('/')[1])
            if not 'DestinationPrefixListId' in str(route) and destination in ip_network(route['DestinationCidrBlock'], strict=False) and mask > most_specific:
                if route['State']  != 'blackhole':
                    most_specific = mask
                    next_hop = route
    #0.0.0.0/0
    if most_specific >= 0 and 'igw-' in str(next_hop):
        return True
    raise Exception('No Internet Gateway route found for destination: ' + events['DestinationIP'] + AWS_DOC)

def check_vpc_peering_connection(events, context):
    vpc_peering_connection = events['VpcPeeringConnection']
    dst_vpc = events['DestinationVpc']
    AWS_DOC = 'https://docs.aws.amazon.com/vpc/latest/peering/working-with-vpc-peering.html'
    if vpc_peering_connection['AccepterVpcInfo']['Region'] == vpc_peering_connection['RequesterVpcInfo']['Region']:
        pass
    else:
        raise Exception('Troubleshooting Cross Region peering connection is not yet supported')
    if dst_vpc != '':
        if dst_vpc in str(vpc_peering_connection):
            pass
        else:
            raise Exception('Kindly check the VPC peering route in route table at the source resource subnet, it does not match the expected destination VPC\n' + AWS_DOC)

    return True

