    output['PeerRoutes'] = routes
    return output

def eval_peer_route_table(events, context):
    from ipaddress import ip_network,ip_address
    routes = events['Routes'] 
    src_ip = ip_address(events['SourceIP']) 
    peering_id = events['PeeringId']       
    most_specific = -1 
    next_hop = {}

    AWS_DOC = '\nWorking with VPC peering connections - https://docs.aws.amazon.com/vpc/latest/peering/working-with-vpc-peering.html'
    for route in routes:
        if route.get('DestinationCidrBlock'):
            mask = int(route['DestinationCidrBlock'].split('/')[1])
            if not 'DestinationPrefixListId' in str(route) and src_ip in ip_network(route['DestinationCidrBlock'], strict=False) and mask > most_specific:
                if route['State'] != 'blackhole':
                    most_specific = mask
                    next_hop = route
    if next_hop.get('VpcPeeringConnectionId') == peering_id:
        return True
    else:
        raise Exception('Destination Subnet route table does not contain a valid peering route for source: ' + events['SourceIP'] + AWS_DOC)

def hop_type_not_supported(events, context):
    nexthop = events['NextHop']
    raise Exception('Automation detected next hop as ' + nexthop + '. Currently, This document supports troubleshooting destinations via local, InternetGateway, NatGateway & VPC Peering only. This document will now perform source side checks only.')
