def script_handler(events, context):
    import boto3
    import time
    import re

    RETENTION_DAYS = 14
    ACCOUNT_ID = context["global:ACCOUNT_ID"]
    region = context["global:REGION"]
    ExecutionId = context["automation:EXECUTION_ID"]
    LogDestinationType = events["LogDestinationType"]
    DeliverLogsPermissionArn = events["DeliverLogsPermissionArn"]
    LogDestinationARN = events["LogDestinationARN"]

    if LogDestinationType == 'cloud-watch-logs':
        logs = boto3.client('logs')
        iam = boto3.client('iam')
        if LogDestinationARN == '':
            try:
                response = logs.create_log_group(
                    logGroupName='AWSSupport-EnableVPCFlowLogs-'+ExecutionId,
                    tags={
                        'Author': 'Created by AWSSupport-EnableVPCFlowLogs automation'
                    }
                )
                if response['ResponseMetadata']["HTTPStatusCode"] == 200:
                    time.sleep(3)
                    response = logs.put_retention_policy(
                        logGroupName='AWSSupport-EnableVPCFlowLogs-'+ExecutionId,
                        retentionInDays=RETENTION_DAYS
                    )
                    LogDestinationARN = 'arn:aws:logs:'+region+':'+ACCOUNT_ID+':log-group:AWSSupport-EnableVPCFlowLogs-'+ExecutionId+':*'
            except Exception as e:
                if "ResourceAlreadyExistsException" in str(e):
                    LogDestinationARN = 'arn:aws:logs:'+region+':'+ACCOUNT_ID+':log-group:AWSSupport-EnableVPCFlowLogs-'+ExecutionId+':*'
                else:
                    raise Exception("Something went wrong on Log Group Creation, please check: "+"\n"+str(e))
        if DeliverLogsPermissionArn == '':

            ## Create the role
            try: 
                response = iam.create_role(
                    RoleName='AWSSupportCreateFlowLogsRole',
                    AssumeRolePolicyDocument="{\n  \"Version\": \"2012-10-17\",\n  \"Statement\": [\n    {\n      \"Sid\": \"\",\n      \"Effect\": \"Allow\",\n      \"Principal\": {\n        \"Service\": \"vpc-flow-logs.amazonaws.com\"\n      },\n      \"Action\": \"sts:AssumeRole\"\n    }\n  ]\n}",
                    Tags=[
                        {
                            'Key': 'Author',
                            'Value': 'Created by AWSSupport-EnableVPCFlowLogs automation'
                        },
                    ]
                )
                if response["ResponseMetadata"]["HTTPStatusCode"] == 200:
                    DeliverLogsPermissionArn = response["Role"]["Arn"]
            except Exception as e:
                if "EntityAlreadyExists" in str(e):
                    DeliverLogsPermissionArn = 'arn:aws:iam::'+ACCOUNT_ID+':role/AWSSupportCreateFlowLogsRole'
                else:
                    response = logs.delete_log_group(
                        logGroupName='AWSSupport-EnableVPCFlowLogs-'+ExecutionId
                    )
                    raise Exception("Something went wrong, please check: "+"\n"+str(e))
            ## Create and attach policy
            try: 
                time.sleep(3)
                response = iam.put_role_policy(
                    RoleName='AWSSupportCreateFlowLogsRole',
                    PolicyName='AWSSupportCreateFlowLogsPolicy',
                    PolicyDocument="{\n    \"Version\": \"2012-10-17\",\n    \"Statement\": [\n        {\n            \"Action\": [\n                \"logs:CreateLogGroup\",\n                \"logs:CreateLogStream\",\n                \"logs:PutLogEvents\",\n                \"logs:DescribeLogGroups\",\n                \"logs:DescribeLogStreams\"\n            ],\n            \"Effect\": \"Allow\",\n            \"Resource\": \"*\"\n        }\n    ]\n}",
                )
            except Exception as e:
                    response = iam.delete_role(
                        RoleName='AWSSupportCreateFlowLogsRole'
                    )
                    response = logs.delete_log_group(
                        logGroupName='AWSSupport-EnableVPCFlowLogs-'+ExecutionId
                    )
                    raise Exception("Something went wrong, please check: "+"\n"+str(e))
        else:
            try:
                response = iam.get_role(RoleName=re.sub(r'arn:(aws[a-zA-Z-]*)?:iam::\d{12}:role\/', '', DeliverLogsPermissionArn))
            except Exception as e:
                raise Exception("Identity Doesn't Exist in this account \n"+str(e))
    return
