    client = boto3.client('rds')
    SnapshotIds = events['Snapshot_Id']
    Result = {}
    message = "Please follow document 'https://docs.aws.amazon.com/AmazonRDS/latest/UserGuide/USER_ShareSnapshot.html#USER_ShareSnapshot.Encrypted.KeyPolicy' to share KMS key with destination account. The following snapshot(s) are encrypted:  "
    for snaps in SnapshotIds:
        try:
            response = client.describe_db_snapshots(DBSnapshotIdentifier=snaps)
