    return{'EncryptedSnaps':message, 'Result':Result}

import boto3
import botocore
def script_handler(events, context):
    client = boto3.client('rds')
    SnapshotIds= events['Snapshot_Id']
    permissions = events['AccountOperation']
    Accountids= events['Account_Id']
    Result = {}
    for snaps in SnapshotIds:
        if Accountids != ['None']:
            if permissions == 'Add':
                try:
                    response = client.modify_db_snapshot_attribute(
                        DBSnapshotIdentifier=snaps,
                        AttributeName='restore',
                        ValuesToAdd=Accountids
                    )
                    if (response['ResponseMetadata']['HTTPStatusCode']==200):
                                Result[snaps] = 'Snapshot is shared successfully with AccountIds'
                except botocore.exceptions.ClientError as error:
                    Result[snaps] = 'Failed - ' + str(error)
            else:
                try:
                    response = client.modify_db_snapshot_attribute(
                        DBSnapshotIdentifier=snaps,
                        AttributeName='restore',
                        ValuesToRemove=Accountids
                    )
                    if (response['ResponseMetadata']['HTTPStatusCode']==200):
                                Result[snaps] = 'Snapshot sharing removed for provided AccountIds'
                except botocore.exceptions.ClientError as error:
                    Result[snaps] = 'Failed - ' + str(error)
        else:
            Result[snaps] = 'No Account Ids provided, Changing Snapshot permissions to Private'
        return {'Result':Result}

import boto3
import botocore


def script_handler(events, context):
    client = boto3.client("rds")
    SnapshotIds = events["Snapshot_Id"]
    permissions = events["PrivatePerm"]
    ModifiedPermission = {"Yes": "Remove"}
    Result = {}
    Commands = {}
    for snaps in SnapshotIds:
        try:
            response = client.modify_db_snapshot_attribute(
                DBSnapshotIdentifier=snaps,
                AttributeName="restore",
                ValuesToRemove=[
                    "all",
                ],
            )
            if response["ResponseMetadata"]["HTTPStatusCode"] == 200:
                Result[snaps] = "Snapshot is Successfully changed to Private"
                command = (
                    '"aws rds modify-db-snapshot-attribute --db-snapshot-identifier '
                    + snaps
                    + ' --attribute-name restore --values-to-add all"'
                )
                Commands[snaps] = (
                    "You can use AWSCli command "
                    + command
                    + " to make snapshot Public again"
                )
        except botocore.exceptions.ClientError as error:
            Result[snaps] = "Failed - " + str(error)
    return {"Result": Result, "Commands": Commands}
