def evalDirectorySecurityGroup(events,context):
    import json 

    directorySecurityGroups = events['directorySecurityGroups']
    inputCidrs = events['remoteCidrs']
    remoteCidrs = []
    finalOutput = []
    for inputCidr in inputCidrs:
        remoteCidr = inputCidr.replace(inputCidr, inputCidr + "/32")
        remoteCidrs.append(remoteCidr)
    requiredTraffic = json.loads(events['requiredTraffic'])
#    print('RequiredTraffic',requiredTraffic)

    err = False
    sourceCidr = {'network-cidrs':remoteCidrs,'security-groups':[]} 
    err = eval_security_groups_traffic(directorySecurityGroups, sourceCidr, requiredTraffic)

    if err:
        finalOutput.append({ 'Error': 'Required traffic is not allowed in Security Groups. See ExecutionLog for Step 6. Please refer to the following article for more information regarding AD connector Prerequisites https://docs.aws.amazon.com/directoryservice/latest/admin-guide/prereq_connector.html' })
        return finalOutput
    else:
        finalOutput.append({ 'Success': 'Required traffic is allowed in Security Groups' })
        return finalOutput

def eval_security_groups_traffic(sgs, sourceCidr, traffic):
    from ipaddress import ip_network
    try:
        err = False
        for sg in sgs:
            for protocol, ranges in traffic.get('outbound',[]).items():
                for range in ranges:
                    allowed = False
                    for r in sg.get('IpPermissionsEgress', []):
                        if r['IpProtocol'] in (protocol,'-1'): 
                            if (not 'FromPort' in r and not 'ToPort' in r) or (r['FromPort'] <= range[0] and r['ToPort'] >= range[1]): # "Rule allows port %s" % r['fromPort']
                                for user_id_group_pair in r.get('UserIdGroupPairs',[]):
                                    for group_id in sourceCidr.get('security-groups', []):
                                        if user_id_group_pair['GroupId'] == group_id:
                                            allowed = True
                                            break
                                for ip_range in r['IpRanges']:
                                    for network_cidr in sourceCidr.get('network-cidrs', []):
                                        if (ip_network(ip_range['CidrIp'], strict=False).overlaps(ip_network(network_cidr, strict=False))):
                                            allowed = True
                                            break
                    if (not allowed):
                        print('No outbound Security Group rule in', sg['GroupId'],'allows connectivity for',protocol.replace("-1", "all").upper(),'in range:',range)
                        err = True
        return err

    except Exception as e:
        print('Failed to evaluate Security Groups.', str(e))
        return True

def evalAclEntries(events,context):
    from ipaddress import ip_network
    import json
    err = False
    directoryNetworkAcls = events['directoryAcl']
    inputCidrs = events['remoteCidrs']
    remoteCidrs = []
    finalOutput = []
    for inputCidr in inputCidrs:
        remoteCidr = inputCidr.replace(inputCidr, inputCidr + "/32")
        remoteCidrs.append(remoteCidr)
    requiredTraffic = json.loads(events['requiredTraffic'])

    sourceCidr = {'network-cidrs':remoteCidrs,'security-groups':[]} 
    allowRules = eval_acl_rules(directoryNetworkAcls, sourceCidr, requiredTraffic)
    err = check_allowed(allowRules, requiredTraffic)

    if err:
        finalOutput.append({ 'Error': 'Required traffic is not allowed in Network ACLs. See ExecutionLog for Step 7. Please refer to the following article for more information regarding AD connector Prerequisites https://docs.aws.amazon.com/directoryservice/latest/admin-guide/prereq_connector.html' })
        return finalOutput
    else:
        finalOutput.append({ 'Success': 'Required traffic is allowed in Network ACLs' })
        return finalOutput

def print_allowed(allowRules):
    for nacl_id, nacl in allowRules.items():
        print('NetworkAclId', nacl_id, 'Inbound:', list(nacl[0].items()), ' <> Outbound:', list(nacl[1].items()))

def eval_acl_rules(rules, sourceCidr, traffic): 
    from collections import defaultdict
    from ipaddress import ip_network

    in_allow = defaultdict(list)
    out_allow = defaultdict(list)
    allowRules = defaultdict(list)

    try:
        for nacl in rules:
                for r in sorted(nacl['Entries'], key=lambda k: k['RuleNumber'], reverse=True):
                        for network_cidr in sourceCidr.get('network-cidrs', []):
                            protocol = get_std_protocol(r['Protocol'])
                            if (ip_network(r['CidrBlock'], strict=False).overlaps(ip_network(network_cidr, strict=False))):
                                if not 'PortRange' in r:
                                    f=0
                                    t=65535
                                else:
                                    f=r['PortRange']['From']
                                    t=r['PortRange']['To']

                                if not r['Egress']:
                                    if r['RuleAction'] == 'allow':
                                        in_allow[protocol] = range_add([f,t],in_allow[protocol])
                                    else:
                                        in_allow[protocol] = range_diff([f,t],in_allow[protocol])
                                else:
                                    if r['RuleAction'] == 'allow':

                                        out_allow[protocol] = range_add([f,t],out_allow[protocol])
                                    else:
                                        out_allow[protocol] = range_diff([f,t],out_allow[protocol])

                allowRules[nacl['NetworkAclId']].append(in_allow.copy())#[0]
                allowRules[nacl['NetworkAclId']].append(out_allow.copy())#[1]
                in_allow.clear()
                out_allow.clear()

        return allowRules

    except Exception as e:
        print('Failed to evaluate Network ACLs.', str(e))
        return allowRules

