def range_add(new,interv):
    interv.append(new)
    interv = [x for x in interv if x != [] and len(x) == 2 and all(isinstance(val, int) for val in x)]
    interv.sort()
    res=[]
    while(len(interv)>0):
        if len(interv)==1:
            res.append(interv[0])
            interv.pop(0)
            continue
        if interv[0][1]>=interv[1][0]:
            tmp=[interv[0][0],max(interv[0][1],interv[1][1])]
            interv[0]=tmp
            interv.pop(1)
            continue
        res.append(interv[0])
        interv.pop(0)
    return res

def range_diff(new,interv):
    interv = range_add([],interv)
    if len(new)==0:
        new = [0,0]
    interv = [x for x in interv if x != [] and len(x) == 2 and all(isinstance(val, int) for val in x)]
    interv.sort()
    res=[]
    for t in interv:
        s1, e1 = t
        s2, e2 = new
        s = sorted((s1, s2, e1, e2))
        if s[0] == s1 and s[0] != s[1]:
            v = s[1] if e1 < s2 else s[1]-1 
            res.append([s[0],v])
        if s[3] == e1 and s[2] != s[3]:
            v = s[2] if e2 < s1 else s[2]+1
            res.append([v,s[3]])
    if len(res)==0:
        res = [[0,0]]
    return res

def check_allowed(allowRules, traffic):
    err = False
    for nacl_id, nacl in allowRules.items():
        for protocol, ranges in traffic.get('inbound',[]).items():
            for range in ranges:
                allowed = False
                for r in nacl[0].get('-1', []):
                    if r[0] <= range[0] and r[1] >= range[1]:
                        allowed = True
                        break
                for r in nacl[0].get(protocol, []):
                    if r[0] <= range[0] and r[1] >= range[1]:
                        allowed = True
                        break
            if (not allowed):
                print('Error: No inbound ACL rule in',nacl_id,'allows connectivity for',protocol.replace("-1", "all").upper(),'in range:',range)
                err = True
            else:
                break

        for protocol, ranges in traffic.get('outbound',[]).items():
            for range in ranges:
                allowed = False
                for r in nacl[1].get('-1', []):
                    if r[0] <= range[0] and r[1] >= range[1]:
                        allowed = True
                        break
                for r in nacl[1].get(protocol, []):
                    if r[0] <= range[0] and r[1] >= range[1]:
                        allowed = True
                        break
            if (not allowed):
                print('Error: No outbound ACL rule in',nacl_id,'allows connectivity for',protocol.replace("-1", "all").upper(),'in range:',range)
                err = True
            else:
                break

    return err

def checkVPCEndpoints(events,context):
    import boto3
    from botocore.exceptions import ClientError,ParamValidationError

    client = boto3.client('ec2')

    # Getting current region
    current_session = boto3.session.Session()
    current_region = current_session.region_name

    # Setting Variables for VPC endpoints
    ssmUrl = 'com.amazonaws.' + current_region + '.ssm'
    stsUrl = 'com.amazonaws.' + current_region + '.sts'
    cwUrl = 'com.amazonaws.' + current_region + '.monitoring'
    vpcId = events['directoryVPC']


    try:
        # Checking SSM Enpoint
        ssmResponse = client.describe_vpc_endpoints(Filters=[{'Name':'service-name','Values':[ssmUrl]},{'Name': 'vpc-id','Values': [vpcId]}])
        if ssmResponse['VpcEndpoints']:
            ssmOutput = { 'Error': 'VPC endpoint for AWS Systems Manager found for the same VPC as of the directory. Please remove the endpoint to fix the directory impairment. VPC Endpoint ID: ' + ssmResponse['VpcEndpoints'][0]['VpcEndpointId']}
        else:
            ssmOutput= { 'Success': 'VPC endpoint for AWS Systems Manager not found for the same VPC as of the directory. No Action Required.'}

        # Checking STS Endpoint
        stsResponse = client.describe_vpc_endpoints(Filters=[{'Name':'service-name','Values':[stsUrl]},{'Name': 'vpc-id','Values': [vpcId]}])
        if stsResponse['VpcEndpoints']:
            stsOutput = { 'Error': 'VPC endpoint for AWS Security Token Service (AWS STS) found for the same VPC as of the directory. Please remove the endpoint to fix the directory impairment. VPC Endpoint ID: ' + stsResponse['VpcEndpoints'][0]['VpcEndpointId']}
        else:
            stsOutput = { 'Success': 'VPC endpoint for AWS Security Token Service (AWS STS) not found for the same VPC as of the directory. No Action Required.'}

        # Checking CloudWatch Endpoint
        cwResponse = client.describe_vpc_endpoints(Filters=[{'Name':'service-name','Values':[cwUrl]},{'Name': 'vpc-id','Values': [vpcId]}])
        if cwResponse['VpcEndpoints']:
            cwOutput = { 'Error': 'VPC endpoint for Amazon CloudWatch found for the same VPC as of the directory. Please remove the endpoint to fix the directory impairment. VPC Endpoint ID: ' + cwResponse['VpcEndpoints'][0]['VpcEndpointId']}
        else:
            cwOutput = { 'Success': 'VPC endpoint for Amazon CloudWatch not found for the same VPC as of the directory. No Action Required.'}

        return ssmOutput,stsOutput,cwOutput

    except ClientError as e:
        raise Exception ("[ERROR]",e)
