import json
import re

import boto3
import botocore

s3 = boto3.client("s3")


def get_lambda_function_config(bucket_name):
    lambda_function_config: list = []

    try:
        response = s3.get_bucket_notification_configuration(Bucket=bucket_name)
        if "LambdaFunctionConfigurations" in response:
            lambda_function_config = response["LambdaFunctionConfigurations"]

        return lambda_function_config

    except botocore.exceptions.ClientError as error:
        if error.response["Error"]["Code"] == "NoSuchBucket":
            raise RuntimeError(
                f"Error: The Amazon S3 bucket '{bucket_name}' does not exist. Please make sure if you provided the right bucket for 'S3BucketName' parameter."
            )
        else:
            raise RuntimeError(
                f"Error: Could not retrieve bucket notification configuration for the Amazon S3 bucket '{bucket_name}' due to an error: {str(error)}"
            )


def get_matching_config_list(lambda_function_config, provided_lambda_arn):
    config_list: list = []
    for config in lambda_function_config:
        configured_lambda_arn = config["LambdaFunctionArn"]
        if provided_lambda_arn == configured_lambda_arn:
            config_list.append(config)

    return config_list


def get_special_char_filter_rules(config):
    filter_rules: list = []
    if "Filter" not in config or "Key" not in config["Filter"] or "FilterRules" not in config["Filter"]["Key"]:
        return filter_rules

    pattern = re.compile('[$&+,:;=?@" "]')
    for rule in config["Filter"]["Key"]["FilterRules"]:
        if pattern.search(rule["Value"]) is not None:
            filter_rules.append(
                {
                    "Name": rule["Name"],
                    "Type": "SpecialChar",
                }
            )
        else:
            if rule["Value"] == "*" or rule["Value"] == ".*":
                filter_rules.append(
                    {
                        "Name": rule["Name"],
                        "Type": "Wildcard",
                    }
                )

    return filter_rules


def handler(event, _):
    bucket_name = event.get("S3BucketName", "")
    lambda_arn = event.get("LambdaFunctionArn", "")

    report_str = (
        "\n{:-<55}".format("")
        + "\n{:^55}".format("S3 Event Notification Configuration Analysis Result")
        + "\n{:-<55}".format("")
        + "\n\n"
    )

    lambda_function_config = get_lambda_function_config(bucket_name)
    matching_config_list = get_matching_config_list(lambda_function_config, lambda_arn)
    if not matching_config_list:
        report_str += (
            "!!! Action Required !!!\n"
            + f"No event notification configuration found for the AWS Lambda function {lambda_arn}. Please create one following the documentation below.\n"
            + "\n"
            + "- Enabling and configuring event notifications using the Amazon S3 console\n"
            + "https://docs.aws.amazon.com/AmazonS3/latest/userguide/enable-event-notifications.html\n"
            + "\n"
        )
        return {"Report": report_str}

    else:
        report_str += (
            f"Event notification configuration found for the AWS Lambda function '{lambda_arn}' as follows:\n\n"
        )

    for config in matching_config_list:
        report_str += (
            f"Configuration ID: {config['Id']}\n" + "---------------\n" + f"* Events: {json.dumps(config['Events'])}\n"
        )
        if "Filter" in config and "Key" in config["Filter"] and "FilterRules" in config["Filter"]["Key"]:
            report_str += f"* Filter Rules: {json.dumps(config['Filter']['Key']['FilterRules'])}\n\n"

        special_char_rules = get_special_char_filter_rules(config)
        if not special_char_rules:
            report_str += "--> No special characters or wildcard character ('*') found in filter rules.\n\n\n"
        else:
            for rule in special_char_rules:
                report_str += f"!!! Action Required !!! (Rule: {rule['Name']}) \n"
                if rule["Type"] == "SpecialChar":
                    report_str += "Special characters found in the value of the rule. You must enter them in URL-encoded (percent-encoded) format: https://www.w3schools.com/tags/ref_urlencode.asp\n\n"
                else:
                    report_str += "Wildcard character '*' found in the value of the rule. A wildcard character '*' cannot be used in filters as a prefix or suffix to represent any character.\n\n"
            report_str += (
                "For more information, see the 'Note' section in the documentation below.\n"
                + "\n"
                + "- Configuring event notifications using object key name filtering\n"
                + "https://docs.aws.amazon.com/AmazonS3/latest/userguide/notification-how-to-filtering.html\n"
            )

    return {"Report": report_str}

import json
import uuid

import boto3
import botocore

_lambda = boto3.client("lambda")


def get_resource_policy(lambda_arn):

    policy: dict = {"Statement": []}
    try:
        response = _lambda.get_policy(FunctionName=lambda_arn)
        policy = json.loads(response["Policy"])
        return policy

    except botocore.exceptions.ClientError as error:
        # Assuming the specified Lambda function (or its version/alias) does not have resource policy configured since function existence has already been confirmed in the previous step
        if error.response["Error"]["Code"] == "ResourceNotFoundException":
            return policy
        else:
            raise RuntimeError(
                f"Error: Could not retrieve resource policy for the AWS Lambda function '{lambda_arn}' due to an error: {str(error)}"
            ) from None


def is_bucket_allowed_to_invoke(policy, bucket):
    is_allowed = False
    for statement in policy["Statement"]:
        if statement["Action"] != "lambda:InvokeFunction":
            continue

        if (
            "Service" not in statement["Principal"]
            or statement["Principal"]["Service"] != "s3.amazonaws.com"
            or statement["Effect"] != "Allow"
        ):
            continue

        if "Condition" not in statement:
            continue

        for key in statement["Condition"]:
            if key != "ArnLike":
                continue

            if statement["Condition"][key]["AWS:SourceArn"].split(":", 6)[5] == bucket:
                is_allowed = True
                break

    return is_allowed


def handler(event, context):
    bucket_name = event.get("S3BucketName", "")
    lambda_arn = event.get("LambdaFunctionArn", "")
    account_id = context["global:ACCOUNT_ID"]

    report_str = (
        "\n{:-<55}".format("")
        + "\n{:^55}".format("Lambda Function Resource Policy Analysis Result")
        + "\n{:-<55}".format("")
        + "\n\n"
    )

    resource_policy = get_resource_policy(lambda_arn)

    if is_bucket_allowed_to_invoke(resource_policy, bucket_name):
        report_str += (
            f"Resource policy permission for the Lambda function '{lambda_arn}' exists and is correct.\n"
            + "If an event type matches and you continue to observe an issue, please check and configure your Lambda concurrency to handle concurrent executions of incoming Amazon S3 event notifications.\n\n"
        )
    else:
        statement_id = f"{account_id}_s3_notification_for_lambda_{uuid.uuid4()}"
        report_str += (
            "!!! Action Required !!!\n"
            + f"The provided AWS Lambda function '{lambda_arn}' does not have resource policy configured for the Amazon S3 bucket {bucket_name}. Please configure one by following the tutorial below.\n\n"
            + "- Tutorial: Using an Amazon S3 trigger to invoke a Lambda function\n"
            + "https://docs.aws.amazon.com/lambda/latest/dg/with-s3-example.html\n\n"
            + "Alternatively, you can configure resource policy by executing the following AWS CLI command.\n"
            + "```\n"
            + f"aws lambda add-permission --function-name {lambda_arn} --action lambda:InvokeFunction --statement-id {statement_id} --principal s3.amazonaws.com --source-arn arn:aws:s3:::{bucket_name} --source-account {account_id}\n"
            + "```\n\n"
        )

    return {"Report": report_str}
