import json
import boto3
import botocore
import re

def script_handler(events, context):
    resourcepolicyflag = 0
    result = {}
    result["objkey"] = ' '
    result["Filters"] = ' '
    result["policy"] = ''
    result["response"] = ''
    result["lambdacheck"] = ''
    result["otherLambdafunctionPolicy"] = ''
    result["otherLambdafunctionPolicyresponse"] = ''
    result["Events"] = ''
    functionName = events['functionArn'].split(":",7)[6]
    s3Bucket = events['s3BucketName']
    accountId = events['functionArn'].split(":",7)[4]
    if (len(events['functionArn'].split(":")) == 8):
        qualifier = events['functionArn'].split(":",8)[7]
    else:
        qualifier = ""
    client = boto3.client('s3')
    lambdaclient = boto3.client('lambda')
    bucket_notification = client.get_bucket_notification_configuration(Bucket=s3Bucket)

    try:
        test_policy = lambdaclient.get_policy( FunctionName= functionName)
    except botocore.exceptions.ClientError as err:
        if (err.response['Error']['Message'].find('Function not found')!= -1):
            result["lambdacheck"] = "Lambda function does not exist"

    def eventPattern(config,lambdaName):
        result["Events"] += '\n S3 events for the event '+ config['Id'] + ' and the function ' + functionName + ' are:' + str(config['Events']) + '\n'
        try:
            if (result["Filters"].find("Prefix") != -1):
                result["Filters"] += config["Id"] + ' : ' + json.dumps(config['Filter']['Key']['FilterRules']) + '\n'
            else:
                result["Filters"] = config["Id"] + ' : ' + json.dumps(config['Filter']['Key']['FilterRules']) + '\n'
            regex = re.compile('[$&+,:;=?@" "]')
            for i in config['Filter']['Key']['FilterRules']:
                if (regex.search(i['Value']) == None):
                    result["objkey"] += "\n No Special Character found in " + i['Name'] + " for the event " + config["Id"]
                    if (i['Value'] == '*' or i['Value'] == '.*'):
                        result["objkey"] += "\n Wild character * found in the " + i['Name'] + " for the event " + config["Id"] + " A wildcard character ('*') can't be used in filters as a prefix or suffix to represent any character."
                else:
                    result["objkey"] += "\n NOTE: Special characters found in the value of the " + i["Name"] +  " for the event " + config["Id"] + " ,you must enter them in URL-encoded (percent-encoded) format : https://www.w3schools.com/tags/ref_urlencode.asp"
        except KeyError:
            result["Filters"] = "No event filters found"
            result["objkey"] = "No object keys"

    if 'LambdaFunctionConfigurations' in bucket_notification and result["lambdacheck"] != 'Lambda function does not exist':
        match = 0
        for config in bucket_notification['LambdaFunctionConfigurations']:
            lambdaName = config['LambdaFunctionArn'].split(":",7)[6]
            if (lambdaName == functionName and len(config['LambdaFunctionArn'].split(":"))==8 and qualifier != ""): 
                if (qualifier == config['LambdaFunctionArn'].split(":",8)[7] and lambdaName == functionName):
                    #check code 
                    match = 1 #match found. This prevents override for EventConfiguration in next loop
                    eventPattern(config,lambdaName)               
            elif (lambdaName == functionName and len(config['LambdaFunctionArn'].split(":"))==7 and qualifier == ""):
                match = 1
                eventPattern(config,lambdaName)
        if match == 0:
            result["EventConfiguration"] = "No Event configuration exists for the mentioned S3 bucket and lambda function"
            result["Filters"] = "No Event Filters as no Event configuration exists"
            result["objkey"] = " "
            result["policy"] = " "
            result["response"] = " "
    else:
            result["EventConfiguration"] = "No Event configuration exists for the mentioned S3 bucket and lambda function"
            result["Filters"] = "No Event Filters as no Event configuration exists"
            result["objkey"] = " "
            result["policy"] = " "
            result["response"] = " "


    if (not ('EventConfiguration' in result) and result["lambdacheck"] != 'Lambda function does not exist'):
        result["EventConfiguration"] = "Event Configuration for the bucket exists"            
        try:
            if qualifier == "":
                resource_policy = lambdaclient.get_policy( FunctionName= functionName)
            else:
                resource_policy = lambdaclient.get_policy( FunctionName= functionName ,Qualifier= qualifier)

            policy = json.loads(resource_policy['Policy'])
            for statement in policy['Statement']:
                if statement['Action'] == 'lambda:InvokeFunction':
                    if statement['Principal']['Service'] == 's3.amazonaws.com' and statement['Effect'] == 'Allow':
                        for key in statement['Condition']:
                            if key == 'ArnLike':
                                if statement['Condition'][key]['AWS:SourceArn'].split(":",6)[5] == s3Bucket:
                                    result["policy"] = "Resource policy permission for the Lambda function "+ functionName  +" exists and is correct. If event type matches, Please check/configure your Lambda concurrency to handle concurrent executions of incoming Amazon S3 event notifications"
                                    result["response"] = '\n'
                                    resourcepolicyflag = 1
            if resourcepolicyflag == 0:
            #Resource policy doesnot exist. Display CLI command for adding resourcepolicy 
                result["policy"] = 'Resource policy for the Lambda function with s3 permissions is missing. Please add below Resourcepolicy to lambda using CLI command or alternatively use Lambda console for adding the Resource Policy. Try testing if the s3 trigger works after adding the below policy'
