                    result["response"] =  "\n ------------- \n aws lambda add-permission --function-name " + functionName + " --action lambda:InvokeFunction --statement-id " + accountId + "_event_permissions_from_" + s3Bucket + "_for_" + functionName + " --principal s3.amazonaws.com --source-arn arn:aws:s3:::"+s3Bucket+ "--source-account "+ accountId + "\n ------------- \n"
                else:
                    result["response"] = "\n ------------- \n aws lambda add-permission --function-name " + functionName + " --action lambda:InvokeFunction --qualifier "+ qualifier + " --statement-id " + accountId + "_event_permissions_from_" + s3Bucket + "_for_" + functionName + " --principal s3.amazonaws.com --source-arn arn:aws:s3:::"+s3Bucket+ "--source-account "+ accountId  + "\n ------------- \n" 
