from urllib.parse import quote


def function_handler(events, context):
    status = str(events.get("Status"))
    cw_log_group_name = str(events.get("LogGroupName"))
    automation_execution_id = str(events.get("AutomationID"))

    partition = context["global:AWS_PARTITION"]
    region = context["global:REGION"]

    url = get_cw_log_group_url(cw_log_group_name, partition, region)

    if status == "SUCCEEDED":
        message = f"Hot threads data collection completed. Please check the CloudWatch log group '{cw_log_group_name}' for more information."

    else:
        message = (
            f"An error occurred, please check the Automation Execution ID '{automation_execution_id}' for more details."
        )

    return {"message": message, "url": url, "log_group": cw_log_group_name}


def encode_log_group_name(string):
    return quote(quote(string, safe=""), safe="").replace("%", "$")


# Function that gets the CloudWatch log group url
def get_cw_log_group_url(cw_log_group_name: str, partition: str, aws_region: str) -> str:
    domain_url_mapping = {
        "aws": "aws.amazon.com",
        "aws-cn": "amazonaws.cn",
        "aws-us-gov": "amazonaws-us-gov.com",
    }
    domain_url = domain_url_mapping[partition]
    encoded_log_group_name = encode_log_group_name(cw_log_group_name)
    return f"https://console.{domain_url}/cloudwatch/home?region={aws_region}#logsV2:log-groups/log-group/{encoded_log_group_name}"
