import time
from datetime import datetime, timedelta, timezone

import boto3
from botocore.config import Config

MAX_ATTEMPTS = 10

config = Config(retries={"max_attempts": 10, "mode": "standard"})
ssm_client = boto3.client("ssm", config=config)


def script_handler(events, context):
    try:
        # Get the current execution details
        attempt = 0
        while attempt < MAX_ATTEMPTS:
            try:
                current_execution = ssm_client.describe_automation_executions(
                    Filters=[{"Key": "ExecutionId", "Values": [context["automation:EXECUTION_ID"]]}]
                )["AutomationExecutionMetadataList"][0]
                break
            except Exception as error:
                attempt += 1
                print(
                    f"Failed to get current execution details for {context['automation:EXECUTION_ID']} - {str(error)}. Retrying..."
                )
                time.sleep(10)
                continue
        else:
            raise RuntimeError(
                f"Failed to get current execution details for {context['automation:EXECUTION_ID']}"
            ) from None

        # Check for other previous/older running automation for the same current document that are not in a final status
        current_execution_start_time = datetime.fromtimestamp(
            current_execution["ExecutionStartTime"].timestamp(), timezone.utc
        )

        # Add 5 seconds to 'StartTimeBefore' to capture executions that started exactly at the same time
        current_execution_start_time += timedelta(seconds=10)

        attempt = 0
        while attempt < MAX_ATTEMPTS:
            try:
                # Describe executions that are not in terminal status
                document_executions = ssm_client.describe_automation_executions(
                    Filters=[
                        {"Key": "DocumentNamePrefix", "Values": [current_execution["DocumentName"]]},
                        {"Key": "ExecutionStatus", "Values": ["InProgress", "Pending", "Cancelling", "Waiting"]},
                        {
                            "Key": "StartTimeBefore",
                            "Values": [current_execution_start_time.strftime("%Y-%m-%dT%H:%M:%SZ")],
                        },
                    ]
                )["AutomationExecutionMetadataList"]
                break
            except Exception as error:
                attempt += 1
                print(
                    f"Failed to get current execution details for {context['automation:EXECUTION_ID']} - {str(error)}. Retrying..."
                )
                time.sleep(10)
                continue
        else:
            raise RuntimeError(
                f"Failed to get current execution details for {context['automation:EXECUTION_ID']}"
            ) from None

        # Check for other previous executions targeting the same resource ID. If any, return an error
        for execution in document_executions:
            execution_id = execution["AutomationExecutionId"]
            if execution_id != current_execution["AutomationExecutionId"]:
                if execution.get("Target", "") == events.get("TargetResourceId"):
                    raise RuntimeError(
                        f"There is another execution of this document already in progress for {events.get('TargetResourceId')} with id {execution['AutomationExecutionId']}",
                    ) from None
                attempt = 0
                while attempt < MAX_ATTEMPTS:
                    try:
                        execution_details = ssm_client.get_automation_execution(AutomationExecutionId=execution_id)[
                            "AutomationExecution"
                        ]
                        break
                    except Exception as error:
                        attempt += 1
                        print(f"Failed to get execution details for {execution_id} - {str(error)}. Retrying...")
                        time.sleep(10)
                        continue
                else:
                    raise RuntimeError(f"Failed to get execution details for {execution_id}") from None

                execution_resource_id = next(iter(execution_details), "")
                if execution_resource_id == events.get("TargetResourceId"):
                    raise RuntimeError(
                        f"There is another execution of this document already in progress for {events.get('TargetResourceId')} with id {execution['AutomationExecutionId']}",
                    ) from None

    except Exception as e:
        raise RuntimeError(f"An error occurred when checking concurrent executions: {str(e)}") from None

    return {
        "NoExecutionFound": f"No Automation executions were found in progress for {current_execution.get('DocumentName')} runbook targeting {events.get('TargetResourceId')}"
    }

