from datetime import datetime

import boto3
from botocore.exceptions import ClientError

ERROR_ICON = " \u274c [ERROR]:"
INFO_ICON = " \u2705 [INFO]:"
WARN_ICON = " \u26a0 [WARNING]:"
NON_FAILED_STATUSES = ["Pending", "InProgress", "Success"]
DOCUMENT_NAME = "AWS-RunPatchBaseline"


ssm_client = boto3.client("ssm")


def script_handler(events, context=None):
    instance_id = events["Instance"]
    command_id = events["CommandID"]

    if command_id:
        print(
            f"{INFO_ICON} Looking for the execution '{command_id}' of '{DOCUMENT_NAME}' on the instance '{instance_id}'."
        )

        # Get command invocation details
        result = get_command_invocation(instance_id, command_id)

        # Check for concurrent executions when command_id is provided
        concurrent_command = check_concurrent_patch_executions(
            instance_id, command_id, result["start_time"], result["end_time"]
        )
        if concurrent_command:
            result["concurrent_command_id"] = concurrent_command
        else:
            result["concurrent_command_id"] = "None"

        return result
    else:
        print(
            f"{INFO_ICON} No RunCommandID provided. Looking for latest failed execution of '{DOCUMENT_NAME}' on the instance."
        )

        # First get the latest failed command invocation
        result = get_latest_failed_command_invocation(instance_id)
        failed_command_id = result.get("command")

        # Then check for concurrent executions using the retrieved command ID
        concurrent_command = check_concurrent_patch_executions(
            instance_id, failed_command_id, result["start_time"], result["end_time"]
        )
        if concurrent_command:
            print(f"{WARN_ICON} Found concurrent execution of '{DOCUMENT_NAME}' with command ID: {concurrent_command}")
            result["concurrent_command_id"] = concurrent_command
        else:
            result["concurrent_command_id"] = "None"

        return result


def check_concurrent_patch_executions(instance_id: str, command_id: str, start_time: str, end_time: str):
    """
    Check for concurrent executions of AWS-RunPatchBaseline on the same instance.
    Returns the command ID of concurrent execution if found, None otherwise.
    """
    try:
        # Convert ISO format strings to datetime objects
        original_start_time = datetime.fromisoformat(start_time) if start_time != "Unknown" else None
        original_end_time = datetime.fromisoformat(end_time) if end_time != "Unknown" else None

        if not original_start_time:
            print(f"{WARN_ICON} Could not determine start time of the original command for concurrent execution check.")
            return None

        # If command hasn't finished, use current time as end time
        if not original_end_time:
            original_end_time = datetime.now(original_start_time.tzinfo)

        # Look for other executions in this time window
        paginator = ssm_client.get_paginator("list_command_invocations")
        response_iterator = paginator.paginate(
            InstanceId=instance_id,
            Filters=[
                {
                    "key": "DocumentName",
                    "value": DOCUMENT_NAME,
                },
            ],
            Details=True,
        )

        for page in response_iterator:
            for invocation in page.get("CommandInvocations", []):
                # Skip the original command
                if invocation.get("CommandId") == command_id:
                    continue

                # Check each command's timing
                command_plugins = invocation.get("CommandPlugins", [])
                for plugin in command_plugins:
                    if plugin.get("Name") == "PatchLinux":
                        other_start_time = plugin.get("ResponseStartDateTime")
                        other_end_time = plugin.get("ResponseFinishDateTime")

                        if not other_start_time:
                            continue

                        if not other_end_time:
                            other_end_time = datetime.now(other_start_time.tzinfo)

                        # Check for overlap
                        if other_start_time <= original_end_time and other_end_time >= original_start_time:
                            print(
                                f"{WARN_ICON} Found concurrent execution of '{DOCUMENT_NAME}' "
                                f"(Command ID: {invocation.get('CommandId')})"
                            )
                            return invocation.get("CommandId")

        print(f"{INFO_ICON} No concurrent executions found.")
        return None

    except ClientError as e:
        print(f"{WARN_ICON} Error checking for concurrent executions: {str(e)}")
        return None


def get_command_invocation(instance_id: str, command_id: str) -> dict:
    try:
        # Use get_command_invocation for most of the information
        response = ssm_client.get_command_invocation(InstanceId=instance_id, CommandId=command_id)
        if not response or response.get("DocumentName") != DOCUMENT_NAME:
            raise RuntimeError(f"{ERROR_ICON} Command ID {command_id} was not executed for {DOCUMENT_NAME}.") from None

        status = response.get("Status", "Unknown")

        # Get both start and end time from ListCommandInvocations with details flag
        start_time = "Unknown"
        end_time = "Unknown"

        invocations = ssm_client.list_command_invocations(
            CommandId=command_id, InstanceId=instance_id, Details=True
        ).get("CommandInvocations", [])

        if invocations:
            invocation = invocations[0]
            # Get start and end time from PatchLinux plugin
            command_plugins = invocation.get("CommandPlugins", [])
            for plugin in command_plugins:
                if plugin.get("Name") == "PatchLinux":
                    response_start_time = plugin.get("ResponseStartDateTime")
                    response_finish_time = plugin.get("ResponseFinishDateTime")
                    if response_start_time and isinstance(response_start_time, datetime):
                        start_time = response_start_time.isoformat()
                    if response_finish_time and isinstance(response_finish_time, datetime):
                        end_time = response_finish_time.isoformat()
                    break

        print(f"{INFO_ICON} Found '{DOCUMENT_NAME}' '{command_id}' execution for the instance in status {status}.")
        return {"command": command_id, "status": status, "start_time": start_time, "end_time": end_time}

    except ClientError as e:
        raise RuntimeError(f"{ERROR_ICON} Failed to get command invocation for '{command_id}': {str(e)}") from None


def get_latest_failed_command_invocation(instance_id: str) -> dict:
    print(
        f"{INFO_ICON} No RunCommandID provided. Looking for the latest failed execution of '{DOCUMENT_NAME}' targeting the instance."
    )
    try:
        paginator = ssm_client.get_paginator("list_command_invocations")
        response_iterator = paginator.paginate(
            InstanceId=instance_id,
            Filters=[
                {
                    "key": "DocumentName",
                    "value": DOCUMENT_NAME,
                },
            ],
            Details=True,  # Changed to True to get plugin details
        )

        latest_failed_command_invocation = None
        latest_start_time = None
        command_result = {"command": None, "status": "Unknown", "start_time": "Unknown", "end_time": "Unknown"}

        for page in response_iterator:
            command_invocations = page.get("CommandInvocations", [])
            for invocation in command_invocations:
                if invocation.get("Status") not in NON_FAILED_STATUSES:
                    # Get start and end time from PatchLinux plugin
                    command_plugins = invocation.get("CommandPlugins", [])
                    for plugin in command_plugins:
                        if plugin.get("Name") == "PatchLinux":
                            response_start_time = plugin.get("ResponseStartDateTime")
                            if response_start_time and isinstance(response_start_time, datetime):
                                # Get the most recent execution based on ResponseStartDateTime
                                if not latest_start_time or response_start_time > latest_start_time:
                                    latest_start_time = response_start_time
                                    latest_failed_command_invocation = invocation
                                    command_result["command"] = invocation.get("CommandId")
                                    command_result["status"] = invocation.get("Status", "Unknown")
                                    command_result["start_time"] = response_start_time.isoformat()

                                    response_finish_time = plugin.get("ResponseFinishDateTime")
                                    if response_finish_time and isinstance(response_finish_time, datetime):
                                        command_result["end_time"] = response_finish_time.isoformat()
                            break

        if latest_failed_command_invocation:
            print(
                f"{INFO_ICON} Found '{DOCUMENT_NAME}' '{command_result['command']}' execution on the instance with status '{command_result['status']}'."
            )
            return command_result
        else:
            raise RuntimeError(
                f"{ERROR_ICON} No failed RunCommandID of {DOCUMENT_NAME} found for '{instance_id}'. Rerun this automation providing a RunCommandID that has failed execution."
            ) from None

    except ClientError as e:
        raise RuntimeError(
            f"{ERROR_ICON} Failed to get the latest failed execution of '{DOCUMENT_NAME}' on the instance: {str(e)}"
        ) from None

