import logging

import boto3
from botocore.errorfactory import ClientError

log = logging.getLogger()
log.setLevel(logging.INFO)


def validate_vpc_attrs(events, context):
    result: dict = {
        "Check": {
            "Status": "SKIPPED",
            "Detail": ["DB Instance is not PubliclyAccessible, so this check is not required."],
        },
    }

    # get region VpcId and PubliclyAccessibility
    region = context["global:REGION"]
    vpc_id = events.get("DBVpcId")
    rds_publicly_accessible = events.get("IsPublic")

    if rds_publicly_accessible:
        try:
            ec2 = boto3.client("ec2", region_name=region)

            dns_hostname_value = ec2.describe_vpc_attribute(Attribute="enableDnsHostnames", VpcId=vpc_id)[
                "EnableDnsHostnames"
            ]["Value"]
            dns_support_value = ec2.describe_vpc_attribute(Attribute="enableDnsSupport", VpcId=vpc_id)[
                "EnableDnsSupport"
            ]["Value"]

            if dns_hostname_value and dns_support_value:
                status = "PASSED"
                message_detail = (
                    "Value for both VPC attributes (`enableDnsHostnames` and `enableDnsSupport`) is set to `true`."
                )
            elif not dns_hostname_value and not dns_support_value:
                status = "FAILED"
                message_detail = (
                    "Value for both VPC attributes (`enableDnsHostnames` and `enableDnsSupport`) is set to `false`.\n"
                )
                message_detail += "o Please set the value of the VPC attributes (`enableDnsHostnames` and `enableDnsSupport`) to `true`.\n"
            elif not dns_hostname_value or not dns_support_value:
                vpc_attr = "enableDnsHostnames" if not dns_hostname_value else "EnableDnsSupport"
                status = "FAILED"
                message_detail = f"Value for `{vpc_attr}` VPC Attribute is `false`.\n"
                message_detail += f"o Please set the value of `{vpc_attr}` VPC attribute to `true`.\n"

            if status == "FAILED":
                message_detail += "   [+] View and update DNS attributes for your VPC: https://docs.aws.amazon.com/vpc/latest/userguide/vpc-dns.html#vpc-dns-updating\n"
        except ClientError as error:
            status = "FAILED"
            message_detail = f"Failed to call the API operation - {error.response['Error']['Code']}.\n"
            message_detail += "o Please make sure that the user/role has the permission `ec2:DescribeVpcAttribute`, if the permission is missing please \n  provide the user/role with permissions and re-run the automation.\n"

            log.error(f"Failed to call the API operation - {error}")

        result["Check"]["Status"] = status
        result["Check"]["Detail"] = message_detail.splitlines()

        return result
    else:
        return result

