import logging
from collections import Counter

import boto3
from botocore.errorfactory import ClientError

log = logging.getLogger()
log.setLevel(logging.INFO)


def validate_subnets(events, context):
    result: dict = {
        "Subnets": {},
        "Checks": {"Existance": {"Status": "", "Detail": []}, "FreeIp": {"Status": "", "Detail": []}, "OtherAz": {}},
    }

    # get the required parameters from events and context
    region = context["global:REGION"]
    dbsubnet_group = events.get("DBSubnetGroup")
    primary_az = events.get("PrimaryAZ")
    is_maz = events.get("IsMaz")
    secondary_az = events.get("SecondaryAZ")

    # extract the subnets from the output of the DBSubnetGroup
    result["Subnets"] = {
        i["SubnetIdentifier"]: {"AZ": i["SubnetAvailabilityZone"]["Name"], "RdsAz": "", "IsPresent": ""}
        for i in dbsubnet_group["Subnets"]
    }
    # count the subnets present availability zone-wise
    total_subnets_in_region = dict(Counter([result["Subnets"][i]["AZ"] for i in result["Subnets"]]))

    # variables to hold status of the checks
    subcheck_status = ""
    freeipcheck_status = ""

    # variables to hold the detailed message/recommendations for the checks
    subcheck_detailed_msg = ""
    freeipcheck_detailed_msg = ""

    try:
        ec2 = boto3.client("ec2", region_name=region)

        # gather the information about available subnets and their respective FreeIPs
        describe_subnets_op = ec2.describe_subnets(
            Filters=[{"Name": "subnet-id", "Values": list(result["Subnets"].keys())}]
        )
        available_subnets_ips = {i["SubnetId"]: i["AvailableIpAddressCount"] for i in describe_subnets_op["Subnets"]}

        for subnet in result["Subnets"].keys():
            if subnet in available_subnets_ips.keys():
                result["Subnets"][subnet]["IsPresent"] = True
                result["Subnets"][subnet]["FreeIPs"] = available_subnets_ips[subnet]
            else:
                result["Subnets"][subnet]["IsPresent"] = False

            # check if the subnet is required by RDS or not
            if result["Subnets"][subnet]["AZ"] == primary_az or result["Subnets"][subnet]["AZ"] == secondary_az:
                result["Subnets"][subnet]["RdsAz"] = True
            else:
                result["Subnets"][subnet]["RdsAz"] = False

        if is_maz:
            primaryaz_subnets = {
                i: result["Subnets"][i]["FreeIPs"]
                for i in result["Subnets"]
                if result["Subnets"][i]["AZ"] == primary_az and result["Subnets"][i]["IsPresent"]
            }
            secondaryaz_subnets = {
                i: result["Subnets"][i]["FreeIPs"]
                for i in result["Subnets"]
                if result["Subnets"][i]["AZ"] == secondary_az and result["Subnets"][i]["IsPresent"]
            }

            free_ips_primary = [primaryaz_subnets[i] for i in primaryaz_subnets]
            free_ips_secondary = [secondaryaz_subnets[i] for i in secondaryaz_subnets]

            # subnet existance and FreeIp check for RDS required Az
            if total_subnets_in_region[primary_az] == len(primaryaz_subnets) and total_subnets_in_region[
                secondary_az
            ] == len(secondaryaz_subnets):
                # subnets per availability zone and present subnets count matches
                subcheck_status = "PASSED"
                subcheck_detailed_msg = (
                    f"All subnets in `{primary_az}` and `{secondary_az}` availability zone exists.\n"
                )
            elif (
                total_subnets_in_region[primary_az] == total_subnets_in_region[secondary_az] == 1
                and len(primaryaz_subnets) == len(secondaryaz_subnets) == 1
            ):
                # subnets per availability zone and present subets are equal to 1
                subcheck_status = "PASSED"
                subcheck_detailed_msg = (
                    f"All subnets in `{primary_az}` and `{secondary_az}` availability zone exists.\n"
                )
            elif len(primaryaz_subnets) > 0 and len(secondaryaz_subnets) > 0:
                # atleast one subnet is present per availability zone
                subcheck_status = "PASSED"
                subcheck_detailed_msg = f"Subnet(s) in `{primary_az}` and `{secondary_az}` availability zone exists.\n"
            elif len(primaryaz_subnets) > 0 and len(secondaryaz_subnets) == 0:
                # no subnets in secondary availability zone
                subcheck_status = "FAILED"
                subcheck_detailed_msg = f"Subnets in `{secondary_az}` availability zone does not exists.\n"

                freeipcheck_status = "SKIPPED"
                freeipcheck_detailed_msg = (
                    f"Check was skipped because availability zone `{secondary_az}` does not have the required subnet.\n"
                )
            elif len(primaryaz_subnets) == 0 and len(secondaryaz_subnets) > 0:
                # no subnets present in primary availability zone
                subcheck_status = "FAILED"
                subcheck_detailed_msg = f"Subnets in `{primary_az}` availability zone does not exists.\n"

                freeipcheck_status = "SKIPPED"
                freeipcheck_detailed_msg = (
                    f"Check was skipped because availability zone `{primary_az}` does not have the required subnet.\n"
                )
            else:
                subcheck_status = "FAILED"
                subcheck_detailed_msg = (
                    f"Subnets from both `{primary_az}` and `{secondary_az}` availability zone does not exists.\n"
                )

                freeipcheck_status = "SKIPPED"
                freeipcheck_detailed_msg = f"Check was skipped because both availability zone (`{primary_az}` and `{secondary_az}`) does not have the required subnet(s).\n"

            if not freeipcheck_status:
                if (sum(free_ips_primary) > 0 and sum(free_ips_secondary) == 0) or (
                    sum(free_ips_primary) == 0 and sum(free_ips_secondary) > 0
                ):
                    # either availability zone has 0 IPs
                    freeipcheck_status = "FAILED"
                    freeipcheck_detailed_msg = f"There are no sufficient available IPs in either `{secondary_az}` (FreeIPs: {sum(free_ips_secondary)}) or `{primary_az}` (FreeIPs: {sum(free_ips_primary)}) availability zone.\n"
                elif sum(free_ips_primary) > 16 and sum(free_ips_secondary) > 16:
                    # both availability zone have more than 16
                    freeipcheck_status = "PASSED"
                    freeipcheck_detailed_msg = (
                        f"There are sufficient available IPs in `{primary_az}` and `{secondary_az} availability zone.\n"
                    )
                elif (sum(free_ips_primary) > 16 and sum(free_ips_secondary) < 16) or (
                    sum(free_ips_primary) < 16 and sum(free_ips_secondary) > 16
                ):
                    # either availability zone have less than 16
                    freeipcheck_status = "WARNING"
                    freeipcheck_detailed_msg = f"At the moment, free IPs in availability zone `{secondary_az}` are {sum(free_ips_secondary)} and in `{primary_az}` are {sum(free_ips_primary)}. Recommendation is to have more than 16 FreeIPs.\n"
                elif sum(free_ips_primary) < 16 and sum(free_ips_secondary) < 16:
                    # both availability zone have less than 16
                    freeipcheck_status = "WARNING"
                    freeipcheck_detailed_msg = f"At the moment, free IPs in availability zone `{secondary_az}` are {sum(free_ips_secondary)} and in `{primary_az}` are {sum(free_ips_primary)}. Recommendation is to have more than 16 FreeIPs.\n"
        else:
            primaryaz_subnets = {
                i: result["Subnets"][i]["FreeIPs"]
                for i in result["Subnets"]
                if result["Subnets"][i]["AZ"] == primary_az and result["Subnets"][i]["IsPresent"]
            }
            free_ips_primary = [primaryaz_subnets[i] for i in primaryaz_subnets]

            # subnet existance check for RDS AZ
            if total_subnets_in_region[primary_az] == len(primaryaz_subnets):
                # subnets per availability zone and present subnets count matches
                subcheck_status = "PASSED"
                subcheck_detailed_msg = f"All subnets in `{primary_az}` availability zone exists.\n"
            elif total_subnets_in_region[primary_az] == 1 and len(primaryaz_subnets) == 1:
                # subnets per availability zone and present subets are equal to 1
                subcheck_status = "PASSED"
                subcheck_detailed_msg = f"All subnets in `{primary_az}` availability zone exists.\n"
            elif len(primaryaz_subnets) > 0:
                # atleast one subnet is present per availability zone
                subcheck_status = "WARNING"
                subcheck_detailed_msg = f"Some of the subnet(s) in `{primary_az}` availability zone exists.\n"
            else:
                subcheck_status = "FAILED"
                subcheck_detailed_msg = f"Subnets from `{primary_az}` availability zone does not exists.\n"

                freeipcheck_status = "SKIPPED"
                freeipcheck_detailed_msg = (
                    f"Check was skipped because availability zone `{primary_az}` does not the required subnet.\n"
                )

            if not freeipcheck_status:
                if sum(free_ips_primary) > 16:
                    freeipcheck_status = "PASSED"
                    freeipcheck_detailed_msg = (
                        f"There are sufficient available IPs in `{primary_az}` availability zone.\n"
                    )
                elif sum(free_ips_primary) == 0:
                    freeipcheck_status = "FAILED"
                    freeipcheck_detailed_msg = (
                        f"There are no sufficient available IPs in `{primary_az}` availability zone.\n"
                    )
                else:
                    freeipcheck_status = "WARNING"
                    freeipcheck_detailed_msg = f"At the moment, free IPs in availability zone `{primary_az}` are {sum(free_ips_primary)}. Recommendation is to have more than 16 FreeIPs.\n"

            # checks for other AZs
            remaining_azs = [i for i in total_subnets_in_region if i != primary_az]
            remaining_azchecks = {i: {"Subnet": "", "FreeIP": ""} for i in remaining_azs}

            for az in remaining_azs:
                az_subnets = [i["SubnetId"] for i in describe_subnets_op["Subnets"] if i["AvailabilityZone"] == az]
                az_freeip = [available_subnets_ips[i] for i in available_subnets_ips.keys() if i in az_subnets]

                # subnet check
                if len(az_subnets) == total_subnets_in_region[az]:
                    remaining_azchecks[az]["Subnet"] = "PASSED"
                elif len(az_subnets) < total_subnets_in_region[az]:
                    remaining_azchecks[az]["Subnet"] = "WARNING"
                else:
                    remaining_azchecks[az]["Subnet"] = "FAILED"

                # freeip check
                if sum(az_freeip) > 16:
                    remaining_azchecks[az]["FreeIP"] = "PASSED"
                elif sum(az_freeip) < 16 and sum(az_freeip) > 0:
                    remaining_azchecks[az]["FreeIP"] = "WARNING"
                else:
                    remaining_azchecks[az]["FreeIP"] = "FAILED"

                result["Checks"]["OtherAz"] = remaining_azchecks

        # add recommendation
        if subcheck_status == "FAILED":
            subcheck_detailed_msg += "o Please restore the database from a latest snapshot that was taken, \n   it is also recommended to take a snapshot before you perform Modify/Stop operation for the instance.\n"
            subcheck_detailed_msg += "   [+] Restoring from a DB snapshot : https://docs.aws.amazon.com/AmazonRDS/latest/UserGuide/USER_RestoreFromSnapshot.html\n"

        if freeipcheck_status != "PASSED":
            freeipcheck_detailed_msg += (
                "o Please free up some IPs before performing Modify/Stop opeartion on the instance.\n"
            )
            freeipcheck_detailed_msg += "   [+] Learn why a subnet in your VPC has insufficient IP addresses : https://repost.aws/knowledge-center/subnet-insufficient-ips\n"
    except ClientError as error:
        subcheck_status = "FAILED"
        subcheck_detailed_msg = f"Failed to call the API operation - {error.response['Error']['Code']}.\n"
        subcheck_detailed_msg += "o Please make sure that the user/role has the permission `ec2:DescribeSubnets`, if the permission is missing \n  please provide the user/role with permissions and re-run the automation.\n"

        freeipcheck_status = "FAILED"
        freeipcheck_detailed_msg = f"Failed to call the API operation - {error.response['Error']['Code']}.\n"

        log.error(f"Failed to call the API operation - {error}")

    result["Checks"]["Existance"]["Status"] = subcheck_status
    result["Checks"]["Existance"]["Detail"] = subcheck_detailed_msg.splitlines()

    result["Checks"]["FreeIp"]["Status"] = freeipcheck_status
    result["Checks"]["FreeIp"]["Detail"] = freeipcheck_detailed_msg.splitlines()

    return result
